import React, { useEffect } from "react";
import '../../component/grid.css';
import homeStyles from '../home/home.module.css';
import '../../component/style.css';
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import TruyenTheoQuangCao from "../truyentheoquangcao"; // Đổi import
import RenderTruyen from "../renderTruyen"; // Import component hiển thị

const Home = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    const maQuangCao1 = 'QC001'; // Thay bằng mã thể loại bạn muốn
    const maQuangCao2 = 'QC002'; // Thay bằng mã thể loại khác
    const maQuangCao3 = 'QC003'; // Thay bằng mã thể loại khác
    const maQuangCao4 = 'lb7ssmtf'; // Thay bằng mã thể loại khác
    const maQuangCao5 = 'lbmcb1jd'; // Thay bằng mã thể loại khác
    const maQuangCao6 = 'lbwst2lr'; // Thay bằng mã thể loại khác
    const maQuangCao7 = 'lc4ymoui'; // Thay bằng mã thể loại khác
    const maQuangCao8 = 'lcf1sre5'; // Thay bằng mã thể loại khác
    const maQuangCao9 = 'lcr3g6f2'; // Thay bằng mã thể loại khác

    const { truyens: theLoai1Data, tentheloai: tenTL1, loading: loadingTL1, error: errorTL1 } = TruyenTheoQuangCao({ matheloai: maTheLoai1 });
    const { truyens: theLoai2Data, tentheloai: tenTL2, loading: loadingTL2, error: errorTL2 } = TruyenTheoQuangCao({ matheloai: maTheLoai2 });
    const { truyens: theLoai3Data, tentheloai: tenTL3, loading: loadingTL3, error: errorTL3 } = TruyenTheoQuangCao({ matheloai: maTheLoai3 });
    const { truyens: theLoai4Data, tentheloai: tenTL4, loading: loadingTL4, error: errorTL4 } = TruyenTheoQuangCao({ matheloai: maTheLoai4 });
    const { truyens: theLoai5Data, tentheloai: tenTL5, loading: loadingTL5, error: errorTL5 } = TruyenTheoQuangCao({ matheloai: maTheLoai5 });
    const { truyens: theLoai6Data, tentheloai: tenTL6, loading: loadingTL6, error: errorTL6 } = TruyenTheoQuangCao({ matheloai: maTheLoai6 });
    const { truyens: theLoai7Data, tentheloai: tenTL7, loading: loadingTL7, error: errorTL7 } = TruyenTheoQuangCao({ matheloai: maTheLoai7 });
    const { truyens: theLoai8Data, tentheloai: tenTL8, loading: loadingTL8, error: errorTL8 } = TruyenTheoQuangCao({ matheloai: maTheLoai8 });
    const { truyens: theLoai9Data, tentheloai: tenTL9, loading: loadingTL9, error: errorTL9 } = TruyenTheoQuangCao({ matheloai: maTheLoai9 });

    return (
        <>
            {loadingTL1 ? <p>Đang tải {tenTL1 || maTheLoai1}...</p> : errorTL1 ? <p>Lỗi tải {tenTL1 || maTheLoai1}: {errorTL1.message}</p> : (
                <RenderTruyen
                    truyens={theLoai1Data.slice(0, 5)}
                    tieude={tenTL1}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5} // Explicitly set the limit
                />
            )}
            {loadingTL2 ? <p>Đang tải {tenTL2 || maTheLoai2}...</p> : errorTL2 ? <p>Lỗi tải {tenTL2 || maTheLoai2}: {errorTL2.message}</p> : (
                <RenderTruyen
                    truyens={theLoai2Data.slice(0, 5)}
                    tieude={tenTL2}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            {loadingTL3 ? <p>Đang tải {tenTL3 || maTheLoai3}...</p> : errorTL3 ? <p>Lỗi tải {tenTL3 || maTheLoai3}: {errorTL3.message}</p> : (
                <RenderTruyen
                    truyens={theLoai3Data.slice(0, 5)}
                    tieude={tenTL3}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_1} alt="Banner 1" />
            {loadingTL4 ? <p>Đang tải {tenTL4 || maTheLoai4}...</p> : errorTL4 ? <p>Lỗi tải {tenTL4 || maTheLoai4}: {errorTL4.message}</p> : (
                <RenderTruyen
                    truyens={theLoai4Data.slice(0, 5)}
                    tieude={tenTL4}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_2} alt="Banner 2" />
            {loadingTL5 ? <p>Đang tải {tenTL5 || maTheLoai5}...</p> : errorTL5 ? <p>Lỗi tải {tenTL5 || maTheLoai5}: {errorTL5.message}</p> : (
                <RenderTruyen
                    truyens={theLoai5Data.slice(0, 5)}
                    tieude={tenTL5}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_3} alt="Banner 3" />
            {loadingTL6 ? <p>Đang tải {tenTL6 || maTheLoai6}...</p> : errorTL6 ? <p>Lỗi tải {tenTL6 || maTheLoai6}: {errorTL6.message}</p> : (
                <RenderTruyen
                    truyens={theLoai6Data.slice(0, 5)}
                    tieude={tenTL6}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_4} alt="Banner 4" />
            {loadingTL7 ? <p>Đang tải {tenTL7 || maTheLoai7}...</p> : errorTL7 ? <p>Lỗi tải {tenTL7 || maTheLoai7}: {errorTL7.message}</p> : (
                <RenderTruyen
                    truyens={theLoai7Data.slice(0, 5)}
                    tieude={tenTL7}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            {loadingTL8 ? <p>Đang tải {tenTL8 || maTheLoai8}...</p> : errorTL8 ? <p>Lỗi tải {tenTL8 || maTheLoai8}: {errorTL8.message}</p> : (
                <RenderTruyen
                    truyens={theLoai8Data.slice(0, 5)}
                    tieude={tenTL8}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
            {loadingTL9 ? <p>Đang tải {tenTL9 || maTheLoai9}...</p> : errorTL9 ? <p>Lỗi tải {tenTL9 || maTheLoai9}: {errorTL9.message}</p> : (
                <RenderTruyen
                    truyens={theLoai9Data.slice(0, 5)}
                    tieude={tenTL9}
                    on_xemthem={false}
                    on_phantrang={false}
                    soLuongHienThi={5}
                />
            )}
        </>
    );
}

export default Home;
