// useDiaChi.js
import { useState, useEffect } from 'react';
import useFetchData from '../useFetchData';

const API_URL_PROVINCES = 'https://provinces.open-api.vn/api/';

export const useDiaChi = () => {
    const [provinceList, setProvinceList] = useState([]);
    const [districtList, setDistrictList] = useState([]);
    const [wardList, setWardList] = useState([]);
    const [selectedProvinceCode, setSelectedProvinceCode] = useState(''); // Đổi tên biến
    const [selectedDistrictCode, setSelectedDistrictCode] = useState(''); // Đổi tên biến
    const [selectedWardCode, setSelectedWardCode] = useState(''); // Đổi tên biến

    const { data: provincesData, loading: provincesLoading, error: provincesError } = useFetchData(API_URL_PROVINCES);

    useEffect(() => {
        if (provincesData) {
            setProvinceList(provincesData);
        }
    }, [provincesData]);

    useEffect(() => {
        if (selectedProvinceCode) { // Đổi tên biến
            fetch(`https://provinces.open-api.vn/api/p/${selectedProvinceCode}?depth=2`) // Giữ nguyên
                .then(response => response.json())
                .then(data => {
                    if (data?.districts) {
                        setDistrictList(Object.values(data.districts));
                        setWardList([]);
                        setSelectedDistrictCode(''); // Đổi tên biến
                        setSelectedWardCode(''); // Đổi tên biến
                    } else {
                        setDistrictList([]);
                        setWardList([]);
                        setSelectedDistrictCode(''); // Đổi tên biến
                        setSelectedWardCode(''); // Đổi tên biến
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu quận/huyện:', error));
        } else {
            setDistrictList([]);
            setWardList([]);
            setSelectedDistrictCode(''); // Đổi tên biến
            setSelectedWardCode(''); // Đổi tên biến
        }
    }, [selectedProvinceCode]); // Đổi tên biến

    useEffect(() => {
        if (selectedDistrictCode && selectedProvinceCode) { // Đổi tên biến
            fetch(`https://provinces.open-api.vn/api/d/${selectedDistrictCode}?depth=2`) // Giữ nguyên
                .then(response => response.json())
                .then(data => {
                    if (data?.wards) {
                        setWardList(Object.values(data.wards));
                        setSelectedWardCode(''); // Đổi tên biến
                    } else {
                        setWardList([]);
                        setSelectedWardCode(''); // Đổi tên biến
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu phường/xã:', error));
        } else {
            setWardList([]);
            setSelectedWardCode(''); // Đổi tên biến
        }
    }, [selectedDistrictCode, selectedProvinceCode]); // Đổi tên biến

    const handleProvinceChange = (event) => {
        setSelectedProvinceCode(event.target.value); // Đổi tên biến
    };

    const handleDistrictChange = (event) => {
        setSelectedDistrictCode(event.target.value); // Đổi tên biến
    };

    const handleWardChange = (event) => {
        setSelectedWardCode(event.target.value); // Đổi tên biến
    };

    return {
        provinceList,
        districtList,
        wardList,
        selectedProvinceCode, // Đổi tên biến
        selectedDistrictCode, // Đổi tên biến
        selectedWardCode, // Đổi tên biến
        handleProvinceChange,
        handleDistrictChange,
        handleWardChange,
        provinprovincesLoading,
        provincesError,
    };
};
