// useGioHang.js
import { useState, useEffect } from 'react';

// Custom hook để quản lý giỏ hàng
export const useGioHang = () => {
    // State cho object chứa thông tin giỏ hàng (key là ID sản phẩm, value là object sản phẩm)
    const [cartObject, setCartObject] = useState({});
    // State cho mảng các sản phẩm trong giỏ hàng (dạng array để dễ map ra giao diện)
    const [cartItemList, setCartItemList] = useState([]);

    // Effect hook để tải dữ liệu giỏ hàng từ localStorage khi component mount
    useEffect(() => {
        const storedCart = localStorage.getItem('GioHang');
        if (storedCart) {
            try {
                const parsedCart = JSON.parse(storedCart);
                setCartObject(parsedCart);
                setCartItemList(Object.values(parsedCart));
            } catch (error) {
                console.error('Lỗi khi xử lý dữ liệu giỏ hàng đã lưu:', error);
                setCartObject({});
                setCartItemList([]);
            }
        } else {
            setCartObject({});
            setCartItemList([]);
        }
    }, []);

    return {
        cartObject,
        cartItemList,
    };
};
