import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';

const Payment = () => {
    const [phuongThucThanhToan, setPhuongThucThanhToan] = useState('Thanh toán khi nhận hàng');
    const [danhSachTinhThanh, setDanhSachTinhThanh] = useState([]);
    const [danhSachQuanHuyen, setDanhSachQuanHuyen] = useState([]);
    const [danhSachPhuongXa, setDanhSachPhuongXa] = useState([]);
    const [tinhThanhDaChon, setTinhThanhDaChon] = useState('');
    const [quanHuyenDaChon, setQuanHuyenDaChon] = useState('');
    const [phuongXaDaChon, setPhuongXaDaChon] = useState('');
    const [diaChiCuThe, setDiaChiCuThe] = useState('');
    const [gioHangObject, setGioHangObject] = useState({});
    const [danhSachSanPhamGioHang, setDanhSachSanPhamGioHang] = useState([]);
    const [maGiamGiaNhap, setMaGiamGiaNhap] = useState('');
    const [maGiamGiaHopLe, setMaGiamGiaHopLe] = useState(null);
    const [phanTramGiamGia, setPhanTramGiamGia] = useState(0);
    const [thongBaoGiamGia, setThongBaoGiamGia] = useState('');

    useEffect(() => {
        const gioHangDaLuu = localStorage.getItem('GioHang');
        if (gioHangDaLuu) {
            try {
                const gioHangParsed = JSON.parse(gioHangDaLuu);
                setGioHangObject(gioHangParsed);
                setDanhSachSanPhamGioHang(Object.values(gioHangParsed));
            } catch (error) {
                console.error('Lỗi khi xử lý dữ liệu giỏ hàng đã lưu:', error);
                setGioHangObject({});
                setDanhSachSanPhamGioHang([]);
            }
        } else {
            setGioHangObject({});
            setDanhSachSanPhamGioHang([]);
        }

        fetch('https://provinces.open-api.vn/api/')
            .then(response => response.json())
            .then(data => setDanhSachTinhThanh(data))
            .catch(error => console.error('Lỗi khi lấy dữ liệu tỉnh/thành phố:', error));
    }, []);

    useEffect(() => {
        if (tinhThanhDaChon) {
            fetch(`https://provinces.open-api.vn/api/p/${tinhThanhDaChon}?depth=2`)
                .then(response => response.json())
                .then(data => {
                    if (data?.districts) {
                        setDanhSachQuanHuyen(Object.values(data.districts));
                        setDanhSachPhuongXa([]);
                        setQuanHuyenDaChon('');
                        setPhuongXaDaChon('');
                    } else {
                        setDanhSachQuanHuyen([]);
                        setDanhSachPhuongXa([]);
                        setQuanHuyenDaChon('');
                        setPhuongXaDaChon('');
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu quận/huyện:', error));
        } else {
            setDanhSachQuanHuyen([]);
            setDanhSachPhuongXa([]);
            setQuanHuyenDaChon('');
            setPhuongXaDaChon('');
        }
    }, [tinhThanhDaChon]);

    useEffect(() => {
        if (quanHuyenDaChon && tinhThanhDaChon) {
            fetch(`https://provinces.open-api.vn/api/d/${quanHuyenDaChon}?depth=2`)
                .then(response => response.json())
                .then(data => {
                    if (data?.wards) {
                        setDanhSachPhuongXa(Object.values(data.wards));
                        setPhuongXaDaChon('');
                    } else {
                        setDanhSachPhuongXa([]);
                        setPhuongXaDaChon('');
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu phường/xã:', error));
        } else {
            setDanhSachPhuongXa([]);
            setPhuongXaDaChon('');
        }
    }, [quanHuyenDaChon, tinhThanhDaChon]);

    const handlePaymentMethodChange = (event) => {
        setPhuongThucThanhToan(event.target.value);
    };

    const handleCityChange = (event) => {
        setTinhThanhDaChon(event.target.value);
    };

    const handleDistrictChange = (event) => {
        setQuanHuyenDaChon(event.target.value);
    };

    const handleWardChange = (event) => {
        setPhuongXaDaChon(event.target.value);
    };

    const handleMaGiamGiaInputChange = (event) => {
        setMaGiamGiaNhap(event.target.value);
        setThongBaoGiamGia('');
        setMaGiamGiaHopLe(null);
        setPhanTramGiamGia(0);
    };

    const handleApDungGiamGia = () => {
        const maNhap = maGiamGiaNhap.trim().toUpperCase();

        if (maNhap) {
            axios.get('http://localhost:3001/giamgias/')
                .then(response => {
                    const danhSachGiamGia = response.data;
                    const maKhuyenMai = danhSachGiamGia.find(gg => gg.id.toUpperCase() === maNhap);

                    if (maKhuyenMai) {
                        const ngayHienTai = new Date();
                        const ngayBatDau = new Date(maKhuyenMai.ngaybatdau);
                        const ngayKetThuc = new Date(maKhuyenMai.ngayketthuc);

                        if (ngayHienTai >= ngayBatDau && ngayHienTai <= ngayKetThuc) {
                            setMaGiamGiaHopLe(maKhuyenMai);
                            setPhanTramGiamGia(maKhuyenMai.phantramgiamgia);
                            setThongBaoGiamGia(`Áp dụng mã giảm giá ${maNhap} thành công! Giảm ${maKhuyenMai.phantramgiamgia}%`);
                        } else if (ngayHienTai < ngayBatDau) {
                            setMaGiamGiaHopLe(null);
                            setPhanTramGiamGia(0);
                            setThongBaoGiamGia(`Mã giảm giá ${maNhap} chưa đến thời gian áp dụng!`);
                        } else {
                            setMaGiamGiaHopLe(null);
                            setPhanTramGiamGia(0);
                            setThongBaoGiamGia(`Mã giảm giá ${maNhap} đã hết hạn!`);
                        }
                    } else {
                        setMaGiamGiaHopLe(null);
                        setPhanTramGiamGia(0);
                        setThongBaoGiamGia(`Mã giảm giá ${maNhap} không tồn tại!`);
                    }
                })
                .catch(error => {
                    console.error('Lỗi khi lấy dữ liệu giảm giá:', error);
                    setMaGiamGiaHopLe(null);
                    setPhanTramGiamGia(0);
                    setThongBaoGiamGia('Có lỗi xảy ra khi kiểm tra mã giảm giá.');
                });
        } else {
            setMaGiamGiaHopLe(null);
            setPhanTramGiamGia(0);
            setThongBaoGiamGia('Vui lòng nhập mã giảm giá.');
        }
    };

    const tinhTongTienHang = () => {
        return Object.values(gioHangObject).reduce(
            (tong, sanPham) => tong + sanPham.GiaBanTruyen * sanPham.SoLuongTruyen,
            0
        );
    };

    const tinhTongTienSauGiamGia = () => {
        const tongTienHang = tinhTongTienHang();
        const tienGiamGia = (tongTienHang * phanTramGiamGia) / 100;
        return tongTienHang - tienGiamGia;
    };

    const taoID = () => {
        return Date.now().toString(36).slice(-5) + Math.random().toString(36).slice(-3);
    };

    const formatDateForDatabase = (date) => {
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        return `<span class="math-inline">\{year\}\-</span>{month}-${day}`;
    };

    const layDuLieuDonHang = () => {
        const tinhDaChon = danhSachTinhThanh.find(tinh => tinh.code === parseInt(tinhThanhDaChon, 10));
        const huyenDaChon = danhSachQuanHuyen.find(huyen => huyen.code === parseInt(quanHuyenDaChon, 10));
        const xaDaChon = danhSachPhuongXa.find(xa => xa.code === parseInt(phuongXaDaChon, 10));

        const diaChiGiaoHang = `${diaChiCuThe}, ${xaDaChon?.name || ''}, ${huyenDaChon?.name || ''}, ${tinhDaChon?.name || ''}`;

        const token = localStorage.getItem('token');
        const maKhachHang = token ? JSON.parse(localStorage.getItem('user'))?.id : null;
        const maDonHang = taoID();
        const ngayDatHangFormatted = formatDateForDatabase(new Date());

        const donHang = {
            id: maDonHang,
            makhachhangdathang: maKhachHang,
            manhanvienxulydonhang: null,
            ngaydathang: ngayDatHangFormatted,
            diachigiaohang: diaChiGiaoHang,
            trangthai: 'Chờ xử lý',
            phuongthucthanhtoan: phuongThucThanhToan,
            magiamgia: maGiamGiaHopLe?.id || null,
            tongtien: tinhTongTienSauGiamGia()
        };

        const chiTietDonHangs = danhSachSanPhamGioHang.map(sanPham => ({
            id: taoID(),
            madonhang: maDonHang,
            matruyen: sanPham.ID,
            soluong: sanPham.SoLuongTruyen
        }));

        return { donHang, chiTietDonHangs };
    };

    const handleDatHang = async (event) => {
        event.preventDefault();
        const { donHang, chiTietDonHangs } = layDuLieuDonHang();

        try {
            const responseDonHang = await axios.post('http://localhost:3001/donhangs/', donHang);
            console.log('Đơn hàng đã được tạo:', donHang);

            // Lưu chi tiết đơn hàng và cập nhật số lượng truyện
            for (const chiTiet of chiTietDonHangs) {
                await axios.post('http://localhost:3001/chitietdonhangs/', chiTiet);
                console.log('Chi tiết đơn hàng đã được tạo:', chiTiet);

                try {
                    const responseTruyen = await axios.get(`http://localhost:3001/truyens/${chiTiet.matruyen}`);
                    const truyenHienTai = responseTruyen.data;
                    const soLuongConLai = truyenHienTai.SoLuong - chiTiet.soluong;

                    if (soLuongConLai >= 0) {
                        await axios.put(`http://localhost:3001/truyens/${chiTiet.matruyen}`, { SoLuong: soLuongConLai });
                        console.log(`Đã cập nhật số lượng truyện ${chiTiet.matruyen} còn lại: ${soLuongConLai}`);
                    } else {
                        console.error(`Không đủ số lượng cho truyện ${chiTiet.matruyen}. Đã đặt: ${chiTiet.soluong}, Hiện có: ${truyenHienTai.SoLuong}`);
                        alert(`Rất tiếc, số lượng truyện ${truyenHienTai.TenTruyen} không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return; // Dừng quá trình đặt hàng
                    }
                } catch (error) {
                    console.error(`Lỗi khi lấy hoặc cập nhật số lượng truyện ${chiTiet.matruyen}:`, error);
                    alert(`Đã có lỗi xảy ra khi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return; // Dừng quá trình đặt hàng
                }
            }

            // Xóa giỏ hàng sau khi đặt hàng thành công và đã cập nhật số lượng truyện
            localStorage.removeItem('GioHang');
            setGioHangObject({});
            setDanhSachSanPhamGioHang([]);
            alert('Đặt hàng thành công!');
            window.location.href = '/'; // Chuyển hướng về trang chủ
        } catch (error) {
            console.error('Lỗi khi tạo đơn hàng:', error);
            alert('Đã có lỗi xảy ra khi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {danhSachSanPhamGioHang.length > 0 ? (
                            danhSachSanPhamGioHang.map(sanPham => (
                                <div className={styles['product-item']} key={sanPham.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={sanPham.AnhTruyen} alt={sanPham.TenTruyen} />
                                        <span className={styles['soluongsp']}>{sanPham.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{sanPham.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{sanPham.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaNhap}
                            onChange={handleMaGiamGiaInputChange}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={handleApDungGiamGia}>
                            Áp dụng
                        </button>
                    </div>
                    {thongBaoGiamGia && (
                        <div className={`${maGiamGiaHopLe ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{tinhTongTienHang().toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{(tinhTongTienHang() * phanTramGiamGia / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{tinhTongTienSauGiamGia().toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handleDatHang}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleCityChange}
                        value={tinhThanhDaChon}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {danhSachTinhThanh.map(tinh => (
                            <option key={tinh.code} value={tinh.code}>{tinh.name}</option>
                        ))}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleDistrictChange}
                        value={quanHuyenDaChon}
                        disabled={!tinhThanhDaChon}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {danhSachQuanHuyen.map(quan => (
                            <option key={quan.code} value={quan.code}>{quan.name}</option>
                        ))}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleWardChange}
                        value={phuongXaDaChon}
                        disabled={!quanHuyenDaChon}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {danhSachPhuongXa.map(phuong => (
                            <option key={phuong.code} value={phuong.code}>{phuong.name}</option>
                        ))}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={diaChiCuThe}
                        onChange={(e) => setDiaChiCuThe(e.target.value)}
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={phuongThucThanhToan === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={phuongThucThanhToan === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {phuongThucThanhToan === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
