import React, { useEffect, useState } from "react";
import '../../component/grid.css';
import banner2 from '../banner2/banner2.module.css';
import '../../component/style.css';
import { useParams, Link, useLocation } from 'react-router-dom'; // Import useLocation
import axios from 'axios';

const Banner2 = () => {
    const { id_theloai, id_quangcao } = useParams(); // Lấy id_theloai hoặc id_quangcao
    const [categoryName, setCategoryName] = useState('');
    const [loading, setLoading] = useState(true);
    const location = useLocation(); // Lấy thông tin về đường dẫn hiện tại

    useEffect(() => {
        const fetchCategoryName = async () => {
            setLoading(true);
            let categoryId;
            let apiUrl = '';

            if (location.pathname.startsWith('/theloai/')) {
                categoryId = id_theloai;
                apiUrl = `http://localhost:3001/theloais/${categoryId}`;
            } else if (location.pathname.startsWith('/quangcao/')) {
                categoryId = id_quangcao;
                apiUrl = `http://localhost:3001/quangcaos/${categoryId}`;
            }

            if (apiUrl && categoryId) {
                try {
                    const response = await axios.get(apiUrl);
                    if (response.data) {
                        setCategoryName(response.data.tenquangcao || response.data.tentheloai || '');
                    }
                } catch (error) {
                    console.error("Lỗi khi tải tên danh mục:", error);
                    setCategoryName('Không tìm thấy');
                } finally {
                    setLoading(false);
                }
            } else {
                setLoading(false);
                setCategoryName(''); // Hoặc một giá trị mặc định khác
            }
        };

        fetchCategoryName();
    }, [id_theloai, id_quangcao, location.pathname]); // Theo dõi cả pathname

    if (loading) {
        return (
            <div className={banner2.banner}>
                <div className={`grid wide ${banner2.title}`}>
                    <div className="row">
                        <div className={`col l-12 m-12 c-12 title-text`}>
                            <h2 className="title-top" style={{ marginBottom: 3 }}>
                                Đang tải...
                            </h2>
                            <div className="title-link">
                                <Link className="a-white hover-red" to="/">
                                    Trang chủ
                                </Link>
                                <span>/ Đang tải...</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className={banner2.banner}>
            <div className={`grid wide ${banner2.title}`}>
                <div className="row">
                    <div className={`col l-12 m-12 c-12 title-text`}>
                        <h2 className="title-top" style={{ marginBottom: 3 }}>
                            {categoryName}
                        </h2>
                        <div className="title-link">
                            <Link className="a-white hover-red" to="/">
                                Trang chủ
                            </Link>
                            <span>/ {categoryName}</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Banner2;
