import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';

const Payment = () => {
    // State để quản lý phương thức thanh toán (mặc định là thanh toán khi nhận hàng)
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    // State để lưu trữ giỏ hàng (ban đầu là một object rỗng)
    const [gioHangObject, setGioHangObject] = useState({});
    // State để chuyển đổi giỏ hàng object thành mảng để dễ hiển thị
    const [gioHangArray, setGioHangArray] = useState([]);
    // State cho input mã giảm giá
    const [maGiamGiaInput, setMaGiamGiaInput] = useState('');
    // State lưu trữ phần trăm giảm giá (ban đầu là 0)
    const [phanTramGiamGia, setPhanTramGiamGia] = useState(0);
    // State hiển thị thông báo về mã giảm giá
    const [thongBaoGiamGia, setThongBaoGiamGia] = useState('');
    // State để theo dõi trạng thái đang áp dụng mã giảm giá
    const [isApplyingCoupon, setIsApplyingCoupon] = useState(false);

    // useEffect chạy khi component được mount (hiển thị lần đầu)
    useEffect(() => {
        // Lấy dữ liệu giỏ hàng từ Local Storage
        const storedCart = localStorage.getItem('GioHang');
        // Nếu có dữ liệu giỏ hàng
        if (storedCart) {
            try {
                // Chuyển đổi chuỗi JSON thành object JavaScript
                const parsedCart = JSON.parse(storedCart);
                // Cập nhật state giỏ hàng object
                setGioHangObject(parsedCart);
                // Chuyển đổi object giỏ hàng thành mảng các sản phẩm
                setGioHangArray(Object.values(parsedCart));
            } catch (error) {
                console.error('Lỗi khi đọc giỏ hàng:', error);
                setGioHangObject({});
                setGioHangArray([]);
            }
        } else {
            // Nếu không có dữ liệu giỏ hàng, đặt state về rỗng
            setGioHangObject({});
            setGioHangArray([]);
        }
    }, []); // [] có nghĩa là useEffect này chỉ chạy một lần sau khi component mount

    // Hàm xử lý thay đổi phương thức thanh toán
    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    // Hàm xử lý khi nhập mã giảm giá
    const handleMaGiamGiaInputChange = (event) => {
        setMaGiamGiaInput(event.target.value);
        setThongBaoGiamGia(''); // Reset thông báo
        setPhanTramGiamGia(0); // Reset phần trăm giảm giá
    };

    // Hàm xử lý khi nhấn nút "Áp dụng" mã giảm giá
    const handleApDungGiamGia = (event) => {
        event.preventDefault(); // Ngăn chặn hành động submit form mặc định
        const maNhap = maGiamGiaInput.trim().toUpperCase(); // Lấy mã, loại bỏ khoảng trắng, viết hoa

        // Kiểm tra nếu có mã nhập và không đang trong quá trình áp dụng
        if (maNhap && !isApplyingCoupon) {
            setIsApplyingCoupon(true); // Đánh dấu là đang áp dụng

            // Gọi API để kiểm tra mã giảm giá
            axios.get('http://localhost:3001/giamgias/')
                .then(response => {
                    const danhSachGiamGia = response.data;
                    // Tìm mã giảm giá hợp lệ
                    const maKhuyenMaiHopLe = danhSachGiamGia.find(
                        (gg) => gg.id.toUpperCase() === maNhap && // So sánh ID
                            new Date(gg.ngaybatdau) <= new Date() && // Kiểm tra ngày bắt đầu
                            new Date(gg.ngayketthuc) >= new Date()   // Kiểm tra ngày kết thúc
                    );

                    // Nếu tìm thấy mã hợp lệ
                    if (maKhuyenMaiHopLe) {
                        setPhanTramGiamGia(maKhuyenMaiHopLe.phantramgiamgia);
                        setThongBaoGiamGia(`Áp dụng mã ${maNhap} thành công! Giảm ${maKhuyenMaiHopLe.phantramgiamgia}%`);
                    } else {
                        setPhanTramGiamGia(0);
                        setThongBaoGiamGia(`Mã ${maNhap} không hợp lệ hoặc đã hết hạn!`);
                    }
                })
                .catch(error => {
                    console.error('Lỗi kiểm tra mã giảm giá:', error);
                    setPhanTramGiamGia(0);
                    setThongBaoGiamGia('Lỗi khi kiểm tra mã giảm giá.');
                })
                .finally(() => {
                    setIsApplyingCoupon(false); // Đánh dấu là đã hoàn tất áp dụng
                });
        } else if (!maNhap) {
            setThongBaoGiamGia('Vui lòng nhập mã giảm giá.');
        }
    };

    // Hàm tính tổng tiền sau khi giảm giá
    const calculateTotalPrice = () => {
        const tongTienHang = Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
        const tienGiamGia = (tongTienHang * phanTramGiamGia) / 100;
        return tongTienHang - tienGiamGia;
    };

    // Hàm tính tạm tính (tổng tiền trước khi giảm giá)
    const calculateTamTinh = () => {
        return Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {gioHangArray.length > 0 ? (
                            gioHangArray.map(item => (
                                <div className={styles['product-item']} key={item.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={item.AnhTruyen} alt={item.TenTruyen} />
                                        <span className={styles['soluongsp']}>{item.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{item.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{item.GiaBanTruyen && item.GiaBanTruyen.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    {/* Form nhập mã giảm giá */}
                    <form id="giamgia" className={styles['giamgia']} onSubmit={handleApDungGiamGia}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaInput}
                            onChange={handleMaGiamGiaInputChange}
                        />
                        <button type="submit" className={styles['giamgia-button']} disabled={isApplyingCoupon}>
                            {isApplyingCoupon ? 'Đang kiểm tra...' : 'Áp dụng'}
                        </button>
                    </form>
                    {/* Hiển thị thông báo về mã giảm giá */}
                    {thongBaoGiamGia && (
                        <div className={`${phanTramGiamGia > 0 ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    {/* Hiển thị tổng tiền */}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{calculateTamTinh().toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {/* Hiển thị chiết khấu nếu có */}
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{((calculateTamTinh() * phanTramGiamGia) / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{calculateTotalPrice().toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>
                {/* Phần thông tin giao hàng và thanh toán (đã được lược bỏ bớt cho đơn giản) */}
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    {/* ... Các input chọn địa chỉ (tỉnh/thành phố, quận/huyện, phường/xã) ... */}
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
