import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../product/product.css';
import '../../component/style.css';
import '../product/responsive.css';
import thangsachkimdong1 from '../img/thangsachkimdong1.jpg';

function Product() {
    const [count, setCount] = useState(0);

    // Hàm tính tổng số lượng sản phẩm trong giỏ hàng từ localStorage
    function getTotalQuantity() {
        let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        return Object.values(GioHang).reduce((acc, item) => acc + item.SoLuongTruyen, 0);
    }

    function updateCartCount(count) {
        let SLGioHang = document.querySelectorAll("#giohang .soluong");  // có 2 biến hiển thị, 1 ở pc tablet 1 ở mobile
        SLGioHang.forEach(phanTuSoLuong => {
            phanTuSoLuong.textContent = count < 10 ? count : "9+";
        });
    }

    // useEffect để cập nhật số lượng từ localStorage khi component render
    useEffect(() => {
        setCount(getTotalQuantity()); // Load lại số lượng giỏ hàng
        updateCartCount(getTotalQuantity());
        EventInputTypeNumber();
    }, []);

    function EventInputTypeNumber() {
        const inputSoLuong = document.querySelectorAll('.input-soluong');
        const buttonUp = document.querySelectorAll('.button-up');
        const buttonDown = document.querySelectorAll('.button-down');

        if (inputSoLuong.length > 0) {
            inputSoLuong.forEach(input => {
                input.addEventListener('input', function () {
                    input.value = input.value.replace(/[^0-9]/g, '');
                });

                input.addEventListener('keydown', function (event) {
                    if (event.key === 'Enter') {
                        input.blur();
                    }
                });

                input.addEventListener('blur', function () {
                    if (input.value === '') {
                        input.value = 1;
                    }
                });
            });
        }

        if (buttonUp.length > 0) {
            buttonUp.forEach((button, index) => {
                button.addEventListener('click', function () {
                    const input = inputSoLuong[index];
                    if (input) {
                        input.value = parseInt(input.value) + 1;
                    }
                });
            });
        }

        if (buttonDown.length > 0) {
            buttonDown.forEach((button, index) => {
                button.addEventListener('click', function () {
                    const input = inputSoLuong[index];
                    if (input && parseInt(input.value) > 1) {
                        input.value = parseInt(input.value) - 1;
                    }
                });
            });
        }
    }

    function handleThemVaoGio() {
        let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        const anhTruyen = document.querySelector('.main-product-img').dataset.linkimg;
        const tenTruyen = document.querySelector('.main-product-name').innerText.trim();
        const giaBanTruyen = parseFloat(document.querySelector('.main-product-giaban').textContent.replace(/[^0-9]+/g, ""));
        const soLuongTruyen = parseInt(document.querySelector('.input-soluong').value);
        const tongTien = giaBanTruyen * soLuongTruyen;

        if (GioHang[tenTruyen]) {
            GioHang[tenTruyen].SoLuongTruyen += soLuongTruyen;
            GioHang[tenTruyen].TongTien += tongTien;
        } else {
            GioHang[tenTruyen] = {
                'AnhTruyen': anhTruyen,
                'TenTruyen': tenTruyen,
                'GiaBanTruyen': giaBanTruyen,
                'SoLuongTruyen': soLuongTruyen,
                'TongTien': tongTien
            };
        }

        localStorage.setItem("GioHang", JSON.stringify(GioHang));

        // Cập nhật tổng số lượng sản phẩm trong giỏ hàng
        const newTotal = getTotalQuantity();
        setCount(newTotal);
        updateCartCount(newTotal);
    }

    return (
        <>
            <div className="grid wide">
                <div className="row">
                    <div className="col c-12 m-12 l-12 duongdan">
                        <a className="a-black hover-red" href="/">Trang chủ</a>
                        <span>/</span>
                        <a className="a-black hover-red" href="">Wings Books</a>
                        <span>/</span>
                        <span>Liệu có sai lầm khi tìm kiếm cuộc gặp gỡ định mệnh trong Dungeon? - Tập 13</span>
                    </div>
                </div>
            </div>

            <div className="grid wide">
                <div className="row main-product">
                    <div className="col l-4 m-4 c-12">
                        <img
                            className="main-product-img"
                            src={thangsachkimdong1}
                            data-linkimg={thangsachkimdong1}
                        />
                    </div>
                    <div className="col l-8 m-8 c-12 main-thongtin">
                        <h1 className="main-product-name">
                            LIỆU CÓ SAI LẦM KHI TÌM KIẾM CUỘC GẶP GỠ ĐỊNH MỆNH TRONG DUNGEON? - TẬP 13
                        </h1>
                        <div className="danhgia displayflex">
                            <i className="fas fa-star" style={{ color: "rgb(239,237,106)", marginRight: 5 }} />
                            <i className="fas fa-star" style={{ color: "rgb(239,237,106)", marginRight: 5 }} />
                            <i className="fas fa-star" style={{ color: "rgb(239,237,106)", marginRight: 5 }} />
                            <i className="fas fa-star" style={{ color: "rgb(239,237,106)", marginRight: 5 }} />
                            <i className="far fa-star" style={{ color: "rgb(239,237,106)", marginRight: 5 }} />
                            <span>98 đánh giá | Đã bán: 116</span>
                        </div>
                        <div className="giatien">
                            <span className="main-product-giaban"><b>52,500đ</b></span>
                            <span className="main-product-giagoc"><s><b>105,000đ</b></s></span>
                            <span>(Bạn đã tiết kiệm được 52,500đ)</span>
                        </div>
                        <div className="slmua">
                            <b>Số lượng</b>
                            <div className="ArrowInputTypeNumber">
                                <button className="button-down flex-center"><i className="fas fa-chevron-down" /></button>
                                <input type="text" className="input-soluong" defaultValue={1} />
                                <button className="button-up flex-center"><i className="fas fa-chevron-up" /></button>
                            </div>
                            <button type="submit" className="btnThem" onClick={handleThemVaoGio}>
                                <b>THÊM VÀO GIỎ HÀNG</b>
                            </button>
                            <button type="submit" className="btnMua"><b>MUA NGAY</b></button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
export default Product;
