import React, { useEffect, useState } from "react";
import '../../component/grid.css';
import '../navbar/navbar.css';
import '../../component/style.css';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link để tạo liên kết điều hướng

const Navbar = () => {
    const [theLoais, setTheLoais] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showMenu, setShowMenu] = useState(false); // State để quản lý hiển thị menu danh mục (trên mobile)

    useEffect(() => {
        const fetchTheLoais = async () => {
            setLoading(true);
            try {
                const response = await axios.get('http://localhost:3001/theloais');
                setTheLoais(response.data);
            } catch (error) {
                console.error("Lỗi khi tải danh mục thể loại:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchTheLoais();
    }, []);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    return (
        <div className="grid wide hide-on-mobile">
            <div className="row" style={{ position: "relative" }}>
                <div className="col m-12 l-12">
                    <div className="navbar">
                        <div className="danhmucsanpham">
                            <div className="flex-center" onClick={toggleMenu}>
                                <i
                                    className="fas fa-bars icon-white"
                                    style={{ marginRight: 2 }}
                                />
                                <b>DANH MỤC SẢN PHẨM</b>
                            </div>
                            <div className={`list-box ${showMenu ? 'open' : ''}`}>
                                <ul>
                                    {loading ? (
                                        <li>Đang tải danh mục...</li>
                                    ) : (
                                        theLoais.map(theLoai => (
                                            <li key={theLoai.id} className="list-box-item">
                                                <Link className="danhmuc-a" to={`/theloai/${theLoai.id}`}>
                                                    <i
                                                        className="fas fa-book"
                                                        style={{ fontSize: 16, marginRight: 4 }}
                                                    />
                                                    {theLoai.tentheloai}
                                                </Link>
                                            </li>
                                        ))
                                    )}
                                </ul>
                            </div>
                        </div>
                        <ul className="tintucgioithieu">
                            <li className="tintuc" style={{ marginRight: 20 }}>
                                <div className="tintucgioithieu-title">
                                    TIN TỨC
                                    <i className="fas fa-angle-down" />
                                </div>
                                <div className="list-box">
                                    <ul>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Hoạt động</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Sự kiện</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Điểm sách</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Sách giả - Sách lậu</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">
                                                Lịch phát hành sách định kỳ
                                            </li>
                                        </a>
                                    </ul>
                                </div>
                            </li>
                            <li className="gioithieu">
                                <div className="tintucgioithieu-title">
                                    GIỚI THIỆU
                                    <i className="fas fa-angle-down" />
                                </div>
                                <div className="list-box">
                                    <ul>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Giới thiệu nhà xuất bản</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Tác giả - Tác phẩm</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Công tác xã hội</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Khen thưởng của nhà nước</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Hợp tác quốc tế</li>
                                        </a>
                                        <a className="danhmuc-a" href="">
                                            <li className="list-box-item">Hệ thống nhà sách</li>
                                        </a>
                                    </ul>
                                </div>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Navbar;
