// cartUtils.js
function updateCartCount(cartCountRefPC, cartCountRefMobile) {
    const GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
    const totalQuantity = Object.values(GioHang).reduce((acc, item) => acc + (item.SoLuongTruyen || 0), 0);
    const displayCount = totalQuantity < 10 ? totalQuantity : "9+";

    if (cartCountRefPC && cartCountRefPC.current) {
        cartCountRefPC.current.textContent = displayCount;
    }

    if (cartCountRefMobile && cartCountRefMobile.current) {
        cartCountRefMobile.current.textContent = displayCount;
    }
}

export default updateCartCount;
