import React, { useState, useEffect } from 'react';
import './payment.module.css'; // Import CSS cho trang thanh toán này
import '../../component/grid.css'; // Import CSS cho hệ thống lưới (chia cột)
import '../../component/style.css'; // Import CSS chung cho toàn trang
import hinhAnhQRPay from '../img/qr-pay.jpg'; // Đường dẫn đến hình ảnh QR code thanh toán
import axios from 'axios'; // Thư viện giúp gọi API (lấy dữ liệu từ server)
import { giaiMaToken } from '../decodeToken'; // Hàm để đọc thông tin từ token (nếu bạn dùng đăng nhập)
import { suDungDiaChi } from './useDiaChi'; // Hook tùy chỉnh để quản lý việc chọn địa chỉ
import { suDungGioHang } from './useGioHang'; // Hook tùy chỉnh để lấy thông tin giỏ hàng
import { suDungMaGiamGia as suDungMaGiamGiaDaDoiTen } from './useGiamGia'; // Hook tùy chỉnh để quản lý mã giảm giá
import {
    tinhTongTienCacSanPham, // Hàm tính tổng tiền của tất cả sản phẩm trong giỏ
    tinhTongTienSauKhiGiamGia, // Hàm tính tổng tiền sau khi đã áp dụng mã giảm giá
    taoIDMoi, // Hàm tạo ra một ID duy nhất
    dinhDangNgayThangChoDatabase // Hàm chuyển đổi định dạng ngày tháng phù hợp để lưu vào database
} from './utils';

const Payment = () => {
    // State (biến lưu trữ trạng thái) cho phương thức thanh toán đã chọn
    const [phuongThucThanhToanDaChon, setPhuongThucThanhToanDaChon] = useState('Thanh toán khi nhận hàng');
    // State cho địa chỉ cụ thể mà người dùng nhập (ví dụ: số nhà, tên đường)
    const [diaChiNhaDuong, setDiaChiNhaDuong] = useState('');
    // State cho mã của khách hàng đang đặt hàng (nếu đã đăng nhập)
    const [maKhachHangDangDat, setMaKhachHangDangDat] = useState(null);

    // Sử dụng hook tùy chỉnh 'suDungDiaChi' để quản lý các thông tin về địa chỉ
    const {
        danhSachTinhThanh, // Mảng chứa danh sách tất cả các tỉnh thành
        danhSachQuanHuyen, // Mảng chứa danh sách các quận huyện thuộc tỉnh đã chọn
        danhSachPhuongXa, // Mảng chứa danh sách các phường xã thuộc quận huyện đã chọn
        maTinhThanhDuocChon, // Mã của tỉnh thành mà người dùng đã chọn
        maQuanHuyenDuocChon, // Mã của quận huyện mà người dùng đã chọn
        maPhuongXaDuocChon, // Mã của phường xã mà người dùng đã chọn
        xuLyThayDoiTinhThanh, // Hàm được gọi khi người dùng chọn một tỉnh thành khác
        xuLyThayDoiQuanHuyen, // Hàm được gọi khi người dùng chọn một quận huyện khác
        xuLyThayDoiPhuongXa, // Hàm được gọi khi người dùng chọn một phường xã khác
        dangTaiDanhSachTinhThanh, // Biến boolean (true/false) cho biết đang tải danh sách tỉnh thành hay không
        loiKhiTaiDanhSachTinhThanh, // Biến lưu trữ lỗi nếu có lỗi xảy ra khi tải danh sách tỉnh thành
    } = suDungDiaChi();

    // Sử dụng hook tùy chỉnh 'suDungGioHang' để lấy thông tin về giỏ hàng
    const { gioHang, danhSachSanPhamTrongGioHang } = suDungGioHang();

    // Sử dụng hook tùy chỉnh 'suDungMaGiamGiaDaDoiTen' để quản lý mã giảm giá
    const {
        maGiamGiaNguoiDungNhap, // Mã giảm giá mà người dùng đã nhập vào
        thongTinMaGiamGiaHopLe, // Đối tượng chứa thông tin của mã giảm giá hợp lệ (nếu có)
        phanTramGiamGiaApDung, // Phần trăm giảm giá của mã giảm giá hợp lệ
        thongBaoVeMaGiamGia, // Thông báo hiển thị cho người dùng về trạng thái mã giảm giá
        xuLyThayDoiMaGiamGia, // Hàm được gọi khi người dùng thay đổi nội dung ô nhập mã giảm giá
        xuLyApDungMaGiamGia, // Hàm được gọi khi người dùng nhấn nút áp dụng mã giảm giá
    } = suDungMaGiamGiaDaDoiTen();

    // Hàm useEffect chạy sau khi component được render lần đầu tiên hoặc khi các biến trong mảng [] thay đổi
    useEffect(() => {
        // Lấy token từ bộ nhớ cục bộ của trình duyệt (nếu có)
        const token = localStorage.getItem('token');
        // Kiểm tra xem token có tồn tại hay không
        if (token) {
            try {
                // Giải mã token để lấy thông tin người dùng
                const thongTinNguoiDung = giaiMaToken(token);
                // Lấy mã người dùng từ thông tin đã giải mã (nếu có)
                setMaKhachHangDangDat(thongTinNguoiDung?.ma_nguoi_dung || null);
            } catch (error) {
                // Nếu có lỗi khi giải mã token, in ra lỗi và đặt mã khách hàng là null
                console.error('Lỗi khi giải mã token:', error);
                setMaKhachHangDangDat(null);
            }
        } else {
            // Nếu không có token, đặt mã khách hàng là null
            setMaKhachHangDangDat(null);
        }
    }, []); // Mảng rỗng [] có nghĩa là useEffect này chỉ chạy một lần sau khi component được hiển thị lần đầu

    // Hàm này được gọi khi người dùng thay đổi phương thức thanh toán
    const handlePaymentMethodChange = (event) => {
        // Cập nhật state 'phuongThucThanhToanDaChon' với giá trị mới từ input
        setPhuongThucThanhToanDaChon(event.target.value);
    };

    // Hàm này tạo ra dữ liệu đơn hàng để gửi lên server
    const layThongTinDonHang = () => {
        // Tìm thông tin chi tiết của tỉnh, huyện, xã đã chọn dựa trên mã
        const tinhThanhDuocChonData = danhSachTinhThanh.find(tinh => tinh.code === parseInt(maTinhThanhDuocChon, 10));
        const quanHuyenDuocChonData = danhSachQuanHuyen.find(huyen => huyen.code === parseInt(maQuanHuyenDuocChon, 10));
        const phuongXaDuocChonData = danhSachPhuongXa.find(xa => xa.code === parseInt(maPhuongXaDuocChon, 10));

        // Tạo địa chỉ giao hàng đầy đủ bằng cách kết hợp các phần
        const diaChiGiaoHangDayDu = `${diaChiNhaDuong}, ${phuongXaDuocChonData?.name || ''}, ${quanHuyenDuocChonData?.name || ''}, ${tinhThanhDuocChonData?.name || ''}`;

        // Tạo một ID đơn hàng mới và duy nhất
        const maDonHangMoi = taoIDMoi();

        // Lấy ngày hiện tại và định dạng nó cho phù hợp với database
        const ngayDatHangFormatted = dinhDangNgayThangChoDatabase(new Date());

        // Tạo đối tượng đơn hàng chứa các thông tin cần thiết
        const donHang = {
            id: maDonHangMoi, // ID của đơn hàng
            makhachhangdathang: maKhachHangDangDat, // Mã khách hàng đặt hàng
            manhanvienxulydonhang: null, // Mã nhân viên xử lý đơn hàng (ban đầu là null)
            ngaydathang: ngayDatHangFormatted, // Ngày khách hàng đặt hàng
            diachigiaohang: diaChiGiaoHangDayDu, // Địa chỉ giao hàng đầy đủ
            trangthai: 'Chờ xử lý', // Trạng thái ban đầu của đơn hàng
            phuongthucthanhtoan: phuongThucThanhToanDaChon, // Phương thức thanh toán đã chọn
            magiamgia: thongTinMaGiamGiaHopLe?.id || null, // Mã giảm giá đã áp dụng (nếu có)
            tongtien: tinhTongTienSauKhiGiamGia(tinhTongTienCacSanPham(gioHang), phanTramGiamGiaApDung) // Tổng tiền sau khi giảm giá
        };

        // Tạo mảng chi tiết đơn hàng từ danh sách sản phẩm trong giỏ hàng
        const chiTietDonHang = danhSachSanPhamTrongGioHang.map(sanPham => ({
            id: taoIDMoi(), // ID của chi tiết đơn hàng
            madonhang: maDonHangMoi, // Mã đơn hàng mà chi tiết này thuộc về
            matruyen: sanPham.ID, // Mã của sản phẩm (truyện)
            soluong: sanPham.SoLuongTruyen // Số lượng sản phẩm mua
        }));

        // Trả về đối tượng chứa thông tin đơn hàng và chi tiết đơn hàng
        return { donHang, chiTietDonHang };
    };

    // Hàm này được gọi khi người dùng nhấn nút đặt hàng
    const handlePlaceOrder = async (event) => {
        // Ngăn chặn trình duyệt thực hiện hành động mặc định của form (ví dụ: tải lại trang)
        event.preventDefault();
        // Lấy thông tin đơn hàng và chi tiết đơn hàng từ hàm 'layThongTinDonHang'
        const { donHang, chiTietDonHang } = layThongTinDonHang();

        try {
            // Gọi API để tạo mới một đơn hàng trên server
            const responseDonHang = await axios.post('http://localhost:3001/donhangs/', donHang);
            console.log('Đơn hàng đã được tạo thành công:', donHang);

            // Lặp qua từng chi tiết đơn hàng để tạo các mục chi tiết đơn hàng trên server
            for (const chiTiet of chiTietDonHang) {
                await axios.post('http://localhost:3001/chitietdonhangs/', chiTiet);
                console.log('Chi tiết đơn hàng đã được tạo thành công:', chiTiet);

                try {
                    // Lấy thông tin chi tiết của sản phẩm (truyện) từ server để cập nhật số lượng tồn kho
                    const responseTruyen = await axios.get(`http://localhost:3001/truyens/${chiTiet.matruyen}`);
                    // Giả sử dữ liệu trả về là một mảng, chúng ta lấy phần tử đầu tiên
                    const thongTinTruyenHienTai = responseTruyen.data[0];
                    // Tính toán số lượng còn lại sau khi khách hàng đặt mua
                    const soLuongConLaiSauKhiMua = thongTinTruyenHienTai.soluong - chiTiet.soluong;

                    // Kiểm tra nếu số lượng còn lại không âm (đủ hàng để bán)
                    if (soLuongConLaiSauKhiMua >= 0) {
                        // Gọi API để cập nhật số lượng tồn kho của sản phẩm trên server
                        await axios.put(`http://localhost:3001/truyens/${chiTiet.matruyen}`, { soluong: soLuongConLaiSauKhiMua });
                        console.log(`Đã cập nhật số lượng truyện có mã ${chiTiet.matruyen} còn lại là: ${soLuongConLaiSauKhiMua}`);
                    } else {
                        // Nếu không đủ hàng, thông báo lỗi cho người dùng và dừng quá trình đặt hàng
                        console.error(`Không đủ số lượng cho truyện có mã ${chiTiet.matruyen}. Đã đặt: ${chiTiet.soluong}, Hiện có: ${thongTinTruyenHienTai.soluong}`);
                        alert(`Rất tiếc, số lượng truyện "${thongTinTruyenHienTai.tentruyen}" không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return; // Dừng việc xử lý đơn hàng tiếp theo
                    }
                } catch (error) {
                    // Nếu có lỗi khi lấy hoặc cập nhật thông tin sản phẩm, in ra lỗi và thông báo cho người dùng
                    console.error(`Lỗi khi lấy hoặc cập nhật số lượng truyện có mã ${chiTiet.matruyen}:`, error);
                    alert(`Đã có lỗi xảy ra khi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return; // Dừng việc xử lý đơn hàng tiếp theo
                }
            }

            // Sau khi đặt hàng thành công, xóa thông tin giỏ hàng khỏi bộ nhớ cục bộ
            localStorage.removeItem('GioHang');
            // Hiển thị thông báo đặt hàng thành công cho người dùng
            alert('Đặt hàng thành công!');
            // Chuyển hướng người dùng về trang chủ
            window.location.href = '/';
        } catch (error) {
            // Nếu có lỗi trong quá trình tạo đơn hàng, in ra lỗi và thông báo cho người dùng
            console.error('Lỗi khi tạo đơn hàng:', error);
            alert('Đã có lỗi xảy ra khi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                {/* Phần hiển thị các sản phẩm mà người dùng đã chọn mua */}
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {/* Kiểm tra nếu có sản phẩm nào trong giỏ hàng */}
                        {danhSachSanPhamTrongGioHang.length > 0 ? (
                            // Nếu có sản phẩm, lặp qua từng sản phẩm để hiển thị
                            danhSachSanPhamTrongGioHang.map(sanPham => (
                                <div className={styles['product-item']} key={sanPham.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={sanPham.AnhTruyen} alt={sanPham.TenTruyen} />
                                        <span className={styles['soluongsp']}>{sanPham.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{sanPham.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{sanPham.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            // Nếu không có sản phẩm nào trong giỏ hàng, hiển thị thông báo
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    {/* Phần cho phép người dùng nhập và áp dụng mã giảm giá */}
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required // Bắt buộc người dùng phải nhập nếu muốn áp dụng
                            className={styles['giamgia-input']}
                            value={maGiamGiaNguoiDungNhap}
                            onChange={xuLyThayDoiMaGiamGia} // Gọi hàm khi giá trị input thay đổi
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={xuLyApDungMaGiamGia}>
                            Áp dụng
                        </button>
                    </div>
                    {/* Hiển thị thông báo về trạng thái của mã giảm giá */}
                    {thongBaoVeMaGiamGia && (
                        <div className={`${thongTinMaGiamGiaHopLe ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoVeMaGiamGia}
                        </div>
                    )}
                    {/* Phần hiển thị tổng tiền của đơn hàng */}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{tinhTongTienCacSanPham(gioHang).toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {/* Hiển thị dòng chiết khấu nếu có mã giảm giá được áp dụng */}
                        {phanTramGiamGiaApDung > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{((tinhTongTienCacSanPham(gioHang) * phanTramGiamGiaApDung) / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGiaApDung}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{tinhTongTienSauKhiGiamGia(tinhTongTienCacSanPham(gioHang), phanTramGiamGiaApDung).toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                {/* Form để người dùng nhập địa chỉ giao hàng và chọn phương thức thanh toán */}
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handlePlaceOrder}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    {/* Dropdown (menu thả xuống) để chọn tỉnh thành */}
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required // Bắt buộc người dùng phải chọn
                        onChange={xuLyThayDoiTinhThanh} // Gọi hàm khi người dùng chọn tỉnh khác
                        value={maTinhThanhDuocChon} // Giá trị hiện tại của tỉnh thành đã chọn
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {/* Lặp qua danh sách tỉnh thành để tạo các option trong dropdown */}
                        {danhSachTinhThanh.map(tinh => (
                            <option key={tinh.code} value={tinh.code}>{tinh.name}</option>
                        ))}
                    </select>
                    {/* Dropdown để chọn quận huyện */}
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required // Bắt buộc người dùng phải chọn
                        onChange={xuLyThayDoiQuanHuyen} // Gọi hàm khi người dùng chọn quận/huyện khác
                        value={maQuanHuyenDuocChon} // Giá trị hiện tại của quận huyện đã chọn
                        disabled={!maTinhThanhDuocChon} // Vô hiệu hóa nếu chưa chọn tỉnh thành
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {/* Lặp qua danh sách quận huyện để tạo các option */}
                        {danhSachQuanHuyen.map(huyen => (
                            <option key={huyen.code} value={huyen.code}>{huyen.name}</option>
                        ))}
                    </select>
                    {/* Dropdown để chọn phường xã */}
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required // Bắt buộc người dùng phải chọn
                        onChange={xuLyThayDoiPhuongXa} // Gọi hàm khi người dùng chọn phường/xã khác
                        value={maPhuongXaDuocChon} // Giá trị hiện tại của phường xã đã chọn
                        disabled={!maQuanHuyenDuocChon} // Vô hiệu hóa nếu chưa chọn quận huyện
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {/* Lặp qua danh sách phường xã để tạo các option */}
                        {danhSachPhuongXa.map(xa => (
                            <option key={xa.code} value={xa.code}>{xa.name}</option>
                        ))}
                    </select>
                    {/* Input (ô nhập liệu) cho địa chỉ cụ thể (số nhà, tên đường) */}
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required // Bắt buộc người dùng phải nhập
                        value={diaChiNhaDuong} // Giá trị hiện tại của địa chỉ cụ thể
                        onChange={(e) => setDiaChiNhaDuong(e.target.value)} // Cập nhật state khi người dùng nhập
                    />
                    {/* Phần chọn phương thức thanh toán */}
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        {/* Lựa chọn thanh toán khi nhận hàng */}
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc" // Các radio button cùng name sẽ nhóm lại, chỉ chọn được một
                                value="Thanh toán khi nhận hàng" // Giá trị khi lựa chọn này được chọn
                                required // Bắt buộc người dùng phải chọn một phương thức
                                checked={phuongThucThanhToanDaChon === 'Thanh toán khi nhận hàng'} // Kiểm tra xem phương thức này có đang được chọn không
                                onChange={handlePaymentMethodChange} // Gọi hàm khi người dùng chọn phương thức khác
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        {/* Lựa chọn chuyển khoản ngân hàng */}
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={phuongThucThanhToanDaChon === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {/* Hiển thị thông tin chuyển khoản nếu người dùng chọn phương thức này */}
                    {phuongThucThanhToanDaChon === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={hinhAnhQRPay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    {/* Nút để người dùng hoàn tất việc đặt hàng */}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
