import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import styles from './home/home.module.css';
import categoryStyles from './category/category.module.css';
import CheckScreenSize from './checkScreenSize';
import { editTenTruyen } from "./editTenTruyen";

const SO_TRUYEN_MOI_TRANG = 10;

const TruyenTheoTheLoai = ({ matheloai, soluong = 5, on_xemthem = true, on_phantrang = true }) => {
    const [danhSachTruyen, setDanhSachTruyen] = useState([]);
    const [tenTheLoai, setTenTheLoai] = useState("");
    const [trangHienTai, setTrangHienTai] = useState(1);
    const mobileRefs = useRef([]);

    CheckScreenSize(mobileRefs);

    useEffect(() => {
        const fetchData = async () => {
            try {
                //gọi đồng thời api để lấy ds truyện và ds thể loại
                const [truyensRes, theloaiRes] = await Promise.all([
                    axios.get("http://localhost:3001/truyens"),
                    axios.get("http://localhost:3001/theloais")
                ]);

                //lọc ds truyện theo mã thể loại được truyền vào
                const truyenTheoLoai = truyensRes.data.filter(item => item.matheloai === matheloai);
                setDanhSachTruyen(truyenTheoLoai);

                //tìm kiếm tên thể loại dựa trên mã thể loại
                const theLoaiTimThay = theloaiRes.data.find(tl => String(tl.id).trim() === String(matheloai).trim());
                if (theLoaiTimThay) setTenTheLoai(theLoaiTimThay.tentheloai);
            } catch (error) {
                console.error("Lỗi khi tải dữ liệu:", error);
            }
        };
        fetchData();
    }, [matheloai]);

    const tongSoTruyen = danhSachTruyen.length;
    // Tổng số trang dựa trên số lượng truyện mỗi trang (ceil là hàm làm tròn)
    const soTrang = Math.ceil(tongSoTruyen / SO_TRUYEN_MOI_TRANG);

    // Tính toán index bắt đầu và kết thúc của truyện trên trang hiện tại
    const indexBatDau = on_phantrang ? (trangHienTai - 1) * SO_TRUYEN_MOI_TRANG : 0;
    const indexKetThuc = on_phantrang ? Math.min(indexBatDau + SO_TRUYEN_MOI_TRANG, tongSoTruyen) : Math.min(soluong, tongSoTruyen);
    // Lấy ra danh sách truyện hiển thị trên trang hiện tại
    const truyenHienThi = danhSachTruyen.slice(indexBatDau, indexKetThuc);

    // Hàm thay đổi trang khi người dùng chọn trang khác
    const chuyenTrang = (soTrangMoi) => {
        setTrangHienTai(soTrangMoi);
    };

    // Hàm hiển thị các số trang để người dùng chuyển đổi
    const hienThiSoTrang = () => {
        if (!on_phantrang || soTrang <= 1) return null; // Không hiển thị phân trang nếu tắt hoặc chỉ có 1 trang
        const cacSoTrang = [];
        for (let i = 1; i <= soTrang; i++) {
            cacSoTrang.push(
                <div
                    key={i}
                    className={`${categoryStyles.trang} ${trangHienTai === i ? categoryStyles.trangchon : ''}`}
                    onClick={() => chuyenTrang(i)}
                >
                    <span><b>{i}</b></span>
                </div>
            );
        }
        return cacSoTrang;
    };

    // Nếu không có truyện nào thuộc thể loại, không hiển thị gì
    if (tongSoTruyen === 0) return null;

    return (
        <div className={`grid wide ${styles['list-product']}`}>
            <h2 style={{ margin: "10px auto", textTransform: 'uppercase' }}>{tenTheLoai}</h2>
            <div className={`row ${styles['list-item']}`}>
                {truyenHienThi.map((truyen, index) => (
                    <div
                        key={truyen.id}
                        className={`col l-2-4 m-3 c-4 checkSizeMobile ${styles['col']}`}
                        ref={(el) => (mobileRefs.current[index] = el)}
                    >
                        <div className={styles['product']}>
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className={styles['product-img']}>
                                    <img src={truyen.anhtruyen} alt={truyen.tentruyen} className={styles['product-img']} />
                                </div>
                                <h4 className={styles['product-name']}>{editTenTruyen(truyen.tentruyen)}</h4>
                                <div className={styles['product-price']}>
                                    <span className={styles['current-price']}><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc && <span className={styles['original-price']}><s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s></span>}
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>
            {on_xemthem && (
                <div className="row">
                    <a className={`col l-12 m-12 c-12 a-red ${styles['xemthem']}`} href={`/danhmuc/${matheloai}`}>
                        <span>Xem thêm &gt;&gt;</span>
                    </a>
                </div>
            )}
            {on_phantrang && soTrang > 1 && (
                <div className={categoryStyles.phantrang}>
                    {hienThiSoTrang()}
                </div>
            )}
        </div>
    );
};

export default TruyenTheoTheLoai;
