import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../shoppingcart/shoppingcart.css';
import '../../component/style.css';
import '../home/responsive.css';

function Shoppingcart() {
    let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};

    //thêm sản phẩm và lưu vào localstorage
    const btnThem = document.querySelector('.btnThem')
    btnThem?.addEventListener("click", function () {
        let anhTruyen = document.querySelector('.main-product-img').src
        let tenTruyen = document.querySelector('.main-product-name').innerHTML.trim()
        let giaBanTruyen = parseFloat(document.querySelector('.main-product-giaban').textContent.replace(/[^0-9]+/g, ""))
        let soLuongTruyen = parseInt(document.querySelector('.input-soluong').value)
        let tongTien = parseFloat(giaBanTruyen) * soLuongTruyen

        if (GioHang[tenTruyen] != null) { //dùng tên truyện để quản lý từng sp
            GioHang[tenTruyen].SoLuongTruyen += soLuongTruyen

            let tongTienCu = parseFloat(GioHang[tenTruyen].TongTien)
            let tongTienMoi = tongTienCu + tongTien
            GioHang[tenTruyen].TongTien = tongTienMoi
        }
        else {
            GioHang[tenTruyen] = {
                'AnhTruyen': anhTruyen,
                'TenTruyen': tenTruyen,
                'GiaBanTruyen': giaBanTruyen,
                'SoLuongTruyen': soLuongTruyen,
                'TongTien': tongTien
            }
        }
        localStorage.setItem("GioHang", JSON.stringify(GioHang))
    })


    function updateTamTinh() {
        let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        let tamTinh = 0;

        // Tính tổng tiền tất cả sản phẩm trong giỏ hàng
        for (let item in GioHang) {
            tamTinh += parseFloat(GioHang[item].TongTien);
        }

        const lableTamTinh = document.querySelector('.tamtinh');
        if (lableTamTinh) {
            lableTamTinh.textContent = tamTinh.toLocaleString('vi-VN') + "đ";
        }
    }



    //hiển thị sản phẩm trong giỏ hàng
    function displayCart() {
        let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        let tableBody = document.querySelector('.tableCart tbody');

        if (tableBody != null) {
            tableBody.innerHTML = '';
        }

        for (let item in GioHang) {
            let row = `
            <tr class="cart-product">
                <td>
                    <img class="cart-product-img" src="${GioHang[item].AnhTruyen}" alt="Sản phẩm">
                </td>
                <td>
                    <a class="cart-product-name" style="cursor: pointer;"><h2>${GioHang[item].TenTruyen}</h2></a>
                    <br>
                    <a style="cursor: pointer;" class="cart-product-remove" data-name="${GioHang[item].TenTruyen}">Xóa</a>
                </td>
                <td class="text-center">
                    <span><h3 class="cart-product-giaban">${GioHang[item].GiaBanTruyen.toLocaleString('vi-VN') + "đ"}</h3></span>
                </td>
                <td>
                    <div class="ArrowInputTypeNumber">
                        <button class="button-down flex-center">
                            <i class="fas fa-chevron-down"></i>
                        </button>
                        <input type="text" class="input-soluong" value="${GioHang[item].SoLuongTruyen}">
                        <button class="button-up flex-center">
                            <i class="fas fa-chevron-up"></i>
                        </button>
                    </div>
                </td>
                <td class="text-center">
                    <span><h3 class="cart-product-tonggia">${GioHang[item].TongTien.toLocaleString('vi-VN') + "đ"}</h3></span>
                </td>
            </tr>`;

            if (tableBody != null) {
                tableBody.insertAdjacentHTML('beforeend', row);
            }
        }

        // Sự kiện xóa sản phẩm
        let cartRemove = document.querySelectorAll('.cart-product-remove');
        cartRemove.forEach(button => {
            button.addEventListener('click', function () {
                const tenTruyen = this.getAttribute('data-name');
                if (tenTruyen) {
                    delete GioHang[tenTruyen];
                    localStorage.setItem('GioHang', JSON.stringify(GioHang));
                    updateCartCount();
                    displayCart();
                    updateTamTinh();
                }
            });
        });

        updateTamTinh();
    }



    function updateQuantity(inputChange) {
        const cartProduct = inputChange.closest('.cart-product');
        const tenTruyenChangeQuantity = cartProduct.querySelector('.cart-product-remove').getAttribute('data-name');

        let soLuongTruyenMoi = parseInt(inputChange.value);

        if (isNaN(soLuongTruyenMoi) || soLuongTruyenMoi <= 0) {
            soLuongTruyenMoi = 1;
            inputChange.value = soLuongTruyenMoi;
        }

        const tongTienMoi = soLuongTruyenMoi * GioHang[tenTruyenChangeQuantity].GiaBanTruyen;
        GioHang[tenTruyenChangeQuantity].SoLuongTruyen = soLuongTruyenMoi;
        GioHang[tenTruyenChangeQuantity].TongTien = tongTienMoi;

        localStorage.setItem('GioHang', JSON.stringify(GioHang));

        // Cập nhật trực tiếp phần hiển thị tổng tiền của sản phẩm
        const tongTienElement = cartProduct.querySelector('.cart-product-tonggia');
        if (tongTienElement) {
            tongTienElement.textContent = tongTienMoi.toLocaleString('vi-VN') + "đ";
        }

        updateTamTinh();
    }



    document.addEventListener('change', function (event) {
        if (event.target.classList.contains('input-soluong'))//nếu phần tử kích hoạt sự kiện có class input-soluong thì trả về true
        {
            updateQuantity(event.target);
        }
    })

    displayCart()


    return (
        <div className="grid wide">
            <div className="row">
                <table className="tableCart">
                    <thead className="tieude">
                        <tr>
                            <th style={{ paddingBottom: 2 }} colSpan={2} className="text-center">
                                Sản phẩm
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Đơn giá
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Số lượng
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Tổng giá
                            </th>
                        </tr>
                    </thead>
                    <tbody></tbody>
                </table>
            </div>
            <div className="row">
                <div
                    className="col c-o-4 c-8 m-o-8 m-4 l-o-9 l-3"
                    style={{
                        display: "inline-flex",
                        lineHeight: 3,
                        justifyContent: "flex-end"
                    }}
                >
                    <p
                        className="flex-center">Tạm tính: &nbsp;
                    </p>
                    <b>
                        <p className="tamtinh">0đ</p>
                    </b>
                </div>
                <div className="col c-o-8 c-4 m-o-8 m-4 l-o-9 l-3">
                    <button id="thanhtoan" type="submit">
                        <b>Thanh toán</b>
                    </button>
                </div>
            </div>
        </div>

    )
}
export default Shoppingcart;
