function updateCartCount(cartCountRefPC, cartCountRefMobile) {
    const gioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
    const arrGioHang = Object.values(gioHang);

    let soluong = 0;
    for (let i = 0; i < arrGioHang.length; i++) {
        const sp = arrGioHang[i];
        const soluong = sp.SoLuongTruyen || 0;

        // Cộng số lượng của sản phẩm hiện tại vào tổng số lượng.
        totalQuantity = totalQuantity + soLuongSanPham;
    }

    const displayCount = soluong < 10 ? soluong : '9+';

    const updateRef = (ref) => {
        if (ref?.current) {
            ref.current.textContent = displayCount;
        }
    };

    updateRef(cartCountRefPC);
    updateRef(cartCountRefMobile);
}

export default updateCartCount;
