import React, { useEffect } from "react";
import '../../component/grid.css';
import homeStyles from '../home/home.module.css';
import '../../component/style.css';
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import useTruyenTheoQuangCao from "../truyentheoquangcao";
import renderTruyen from "../renderTruyen";

const Home = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    const maQuangCao1 = 'QC001';
    const maQuangCao2 = 'QC002';
    const maQuangCao3 = 'QC003';
    const maQuangCao4 = 'lb7ssmtf';
    const maQuangCao5 = 'lbmcb1jd';
    const maQuangCao6 = 'lbwst2lr';
    const maQuangCao7 = 'lc4ymoui';
    const maQuangCao8 = 'lcf1sre5';
    const maQuangCao9 = 'lcr3g6f2';


    const { truyens: quangCao1Data, tenquangcao: tenQC1, loading: loadingQC1, error: errorQC1 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao1 });
    const { truyens: quangCao2Data, tenquangcao: tenQC2, loading: loadingQC2, error: errorQC2 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao2 });
    const { truyens: quangCao3Data, tenquangcao: tenQC3, loading: loadingQC3, error: errorQC3 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao3 });
    const { truyens: quangCao4Data, tenquangcao: tenQC4, loading: loadingQC4, error: errorQC4 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao4 });
    const { truyens: quangCao5Data, tenquangcao: tenQC5, loading: loadingQC5, error: errorQC5 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao5 });
    const { truyens: quangCao6Data, tenquangcao: tenQC6, loading: loadingQC6, error: errorQC6 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao6 });
    const { truyens: quangCao7Data, tenquangcao: tenQC7, loading: loadingQC7, error: errorQC7 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao7 });
    const { truyens: quangCao8Data, tenquangcao: tenQC8, loading: loadingQC8, error: errorQC8 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao8 });
    const { truyens: quangCao9Data, tenquangcao: tenQC9, loading: loadingQC9, error: errorQC9 } = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao9 });


    return (
        //         <>
        //             {/* <TruyenTheoQuangCao maquangcao="QC001" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="QC002" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="QC003" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_1} />
        //             <TruyenTheoQuangCao maquangcao="lb7ssmtf" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_2} />
        //             <TruyenTheoQuangCao maquangcao="lbmcb1jd" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_3} />
        //             <TruyenTheoQuangCao maquangcao="lbwst2lr" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_4} />
        //             <TruyenTheoQuangCao maquangcao="lc4ymoui" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="lcf1sre5" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="lcr3g6f2" soluong={5} />
        //  */}
        //         </>

        <>
            {loadingQC1 ? <p>Đang tải {tenQC1 || maQuangCao1}...</p> : errorQC1 ? <p>Lỗi tải {tenQC1 || maQuangCao1}: {errorQC1.message}</p> : (
                <renderTruyen
                    truyens={quangCao1Data.slice(0, 5)} // Lấy 5 truyện đầu tiên
                    tieude={tenQC1}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {loadingQC2 ? <p>Đang tải {tenQC2 || maQuangCao2}...</p> : errorQC2 ? <p>Lỗi tải {tenQC2 || maQuangCao2}: {errorQC2.message}</p> : (
                <renderTruyen
                    truyens={quangCao2Data.slice(0, 5)}
                    tieude={tenQC2}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {loadingQC3 ? <p>Đang tải {tenQC3 || maQuangCao3}...</p> : errorQC3 ? <p>Lỗi tải {tenQC3 || maQuangCao3}: {errorQC3.message}</p> : (
                <renderTruyen
                    truyens={quangCao3Data.slice(0, 5)}
                    tieude={tenQC3}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_1} alt="Banner 1" />
            {loadingQC4 ? <p>Đang tải {tenQC4 || maQuangCao4}...</p> : errorQC4 ? <p>Lỗi tải {tenQC4 || maQuangCao4}: {errorQC4.message}</p> : (
                <renderTruyen
                    truyens={quangCao4Data.slice(0, 5)}
                    tieude={tenQC4}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_2} alt="Banner 2" />
            {loadingQC5 ? <p>Đang tải {tenQC5 || maQuangCao5}...</p> : errorQC5 ? <p>Lỗi tải {tenQC5 || maQuangCao5}: {errorQC5.message}</p> : (
                <renderTruyen
                    truyens={quangCao5Data.slice(0, 5)}
                    tieude={tenQC5}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_3} alt="Banner 3" />
            {loadingQC6 ? <p>Đang tải {tenQC6 || maQuangCao6}...</p> : errorQC6 ? <p>Lỗi tải {tenQC6 || maQuangCao6}: {errorQC6.message}</p> : (
                <renderTruyen
                    truyens={quangCao6Data.slice(0, 5)}
                    tieude={tenQC6}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_4} alt="Banner 4" />
            {loadingQC7 ? <p>Đang tải {tenQC7 || maQuangCao7}...</p> : errorQC7 ? <p>Lỗi tải {tenQC7 || maQuangCao7}: {errorQC7.message}</p> : (
                <renderTruyen
                    truyens={quangCao7Data.slice(0, 5)}
                    tieude={tenQC7}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {loadingQC8 ? <p>Đang tải {tenQC8 || maQuangCao8}...</p> : errorQC8 ? <p>Lỗi tải {tenQC8 || maQuangCao8}: {errorQC8.message}</p> : (
                <renderTruyen
                    truyens={quangCao8Data.slice(0, 5)}
                    tieude={tenQC8}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {loadingQC9 ? <p>Đang tải {tenQC9 || maQuangCao9}...</p> : errorQC9 ? <p>Lỗi tải {tenQC9 || maQuangCao9}: {errorQC9.message}</p> : (
                <renderTruyen
                    truyens={quangCao9Data.slice(0, 5)}
                    tieude={tenQC9}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
        </>
    )
}

export default Home;
