import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Api_product = () => {
    const { id_truyen } = useParams(); // Lấy id_truyen từ URL
    const [truyen, setTruyen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Gọi API để lấy dữ liệu truyện
        axios.get(`http://localhost:3001/truyens/${id_truyen}`)
            .then(response => {
                setTruyen(response.data); // Lưu dữ liệu truyện vào state
                setLoading(false); // Đặt trạng thái loading là false
            })
            .catch(() => {
                setError('Có lỗi xảy ra'); // Xử lý lỗi khi gọi API
                setLoading(false); // Đặt trạng thái loading là false
            });
    }, [id_truyen]); // Gọi lại khi id_truyen thay đổi

    // Log truyen khi đã cập nhật
    useEffect(() => {
        if (truyen) {
            console.log('Truyen đã được cập nhật:', truyen);
        }
    }, [truyen]); // Chạy lại khi truyen thay đổi

    return (
        <div>
            <h1>{truyen.tentruyen}</h1> {/* Hiển thị tên truyện */}
            <p><strong>Tác giả:</strong> {truyen.tacgia}</p>
            <p><strong>Giá bán:</strong> {truyen.giaban} VNĐ</p>
            <p><strong>Giá gốc:</strong> {truyen.giagoc} VNĐ</p>
            <p><strong>ISBN:</strong> {truyen.isbn}</p>
            <p><strong>Định dạng:</strong> {truyen.dinhdang}</p>
            <p><strong>Đối tượng:</strong> {truyen.doituong}</p>
            <p><strong>Số lượng:</strong> {truyen.soluong}</p>
            <p><strong>Số trang:</strong> {truyen.sotrang}</p>
            <p><strong>Trọng lượng:</strong> {truyen.trongluong}</p>
            <p><strong>Ảnh:</strong> <img src={`path/to/images/${truyen.anhtruyen}`} alt={truyen.tentruyen} /></p>
        </div>
    );

};

export default Api_product;
