import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../shoppingcart/shoppingcart.css';
import '../../component/style.css';
import '../home/responsive.css';

function Shoppingcart() {
    const [gioHang, setGioHang] = useState({});

    useEffect(() => {
        const storedCart = JSON.parse(localStorage.getItem("GioHang")) || {};
        setGioHang(storedCart);
    }, []);

    const updateCart = (updatedCart) => {
        setGioHang(updatedCart);
        localStorage.setItem("GioHang", JSON.stringify(updatedCart));
    };

    const updateQuantity = (tenTruyen, newQuantity) => {
        if (newQuantity <= 0) return;

        const updatedCart = { ...gioHang };
        if (updatedCart[tenTruyen]) {
            updatedCart[tenTruyen].SoLuongTruyen = newQuantity;
            updatedCart[tenTruyen].TongTien = newQuantity * updatedCart[tenTruyen].GiaBanTruyen;
            updateCart(updatedCart);
        }
    };

    const removeFromCart = (tenTruyen) => {
        const updatedCart = { ...gioHang };
        delete updatedCart[tenTruyen];
        updateCart(updatedCart);
    };

    const getTamTinh = () => {
        return Object.values(gioHang).reduce((sum, item) => sum + item.TongTien, 0);
    };

    return (
        <div className="grid wide">
            <div className="row">
                <table className="tableCart">
                    <thead className="tieude">
                        <tr>
                            <th colSpan={2} className="text-center">Sản phẩm</th>
                            <th className="text-center">Đơn giá</th>
                            <th className="text-center">Số lượng</th>
                            <th className="text-center">Tổng giá</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(gioHang).map((tenTruyen) => (
                            <tr className="cart-product" key={tenTruyen}>
                                <td>
                                    <img className="cart-product-img" src={gioHang[tenTruyen].AnhTruyen} alt="Sản phẩm" />
                                </td>
                                <td>
                                    <a className="cart-product-name" style={{ cursor: "pointer" }}>
                                        <h2>{gioHang[tenTruyen].TenTruyen}</h2>
                                    </a>
                                    <br />
                                    <a
                                        style={{ cursor: "pointer" }}
                                        className="cart-product-remove"
                                        onClick={() => removeFromCart(tenTruyen)}
                                    >
                                        Xóa
                                    </a>
                                </td>
                                <td className="text-center">
                                    <h3 className="cart-product-giaban">{gioHang[tenTruyen].GiaBanTruyen.toLocaleString('vi-VN')}đ</h3>
                                </td>
                                <td>
                                    <div className="ArrowInputTypeNumber">
                                        <button
                                            className="button-down flex-center"
                                            onClick={() => updateQuantity(tenTruyen, gioHang[tenTruyen].SoLuongTruyen - 1)}
                                        >
                                            <i className="fas fa-chevron-down"></i>
                                        </button>
                                        <input
                                            type="text"
                                            className="input-soluong"
                                            value={gioHang[tenTruyen].SoLuongTruyen}
                                            onChange={(e) => updateQuantity(tenTruyen, parseInt(e.target.value) || 1)}
                                        />
                                        <button
                                            className="button-up flex-center"
                                            onClick={() => updateQuantity(tenTruyen, gioHang[tenTruyen].SoLuongTruyen + 1)}
                                        >
                                            <i className="fas fa-chevron-up"></i>
                                        </button>
                                    </div>
                                </td>
                                <td className="text-center">
                                    <h3 className="cart-product-tonggia">{gioHang[tenTruyen].TongTien.toLocaleString('vi-VN')}đ</h3>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="row">
                <div className="col c-o-4 c-8 m-o-8 m-4 l-o-9 l-3" style={{ display: "inline-flex", lineHeight: 3, justifyContent: "flex-end" }}>
                    <p className="flex-center">Tạm tính: &nbsp;</p>
                    <b>
                        <p className="tamtinh">{getTamTinh().toLocaleString('vi-VN')}đ</p>
                    </b>
                </div>
                <div className="col c-o-8 c-4 m-o-8 m-4 l-o-9 l-3">
                    <button id="thanhtoan" type="submit">
                        <b>Thanh toán</b>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Shoppingcart;
