import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import '../../component/grid.css';
import categoryStyles from '../category/category.module.css'; // Import CSS module
import '../../component/style.css';
import '../home/home.module.css';
import updateCartCount from '../updateCartCount';

const ITEMS_PER_PAGE = 12; // Số lượng truyện hiển thị trên mỗi trang

const Category = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    const { id_theloai } = useParams();
    const [currentPage, setCurrentPage] = useState(1);
    const [totalItems, setTotalItems] = useState(0); // Tổng số truyện thuộc thể loại
    const [truyensOnPage, setTruyensOnPage] = useState([]);

    useEffect(() => {
        const fetchTruyensByCategory = async () => {
            try {
                const response = await fetch(`http://localhost:3001/truyens?matheloai=${id_theloai}`);
                const data = await response.json();
                setTotalItems(data.length);
                const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
                const endIndex = startIndex + ITEMS_PER_PAGE;
                const truyensForPage = data.slice(startIndex, endIndex);
                setTruyensOnPage(truyensForPage);
            } catch (error) {
                console.error("Lỗi khi tải truyện theo thể loại:", error);
                setTotalItems(0);
                setTruyensOnPage([]);
            }
        };

        fetchTruyensByCategory();
    }, [id_theloai, currentPage]);

    const totalPages = Math.ceil(totalItems / ITEMS_PER_PAGE);

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const renderPageNumbers = () => {
        const pageNumbers = [];
        for (let i = 1; i <= totalPages; i++) {
            pageNumbers.push(
                <div
                    key={i}
                    className={`${categoryStyles.trang} ${currentPage === i ? categoryStyles.trangchon : ''}`}
                    onClick={() => handlePageChange(i)}
                >
                    <span>
                        <b>{i}</b>
                    </span>
                </div>
            );
        }

        if (totalPages <= 7) {
            return pageNumbers;
        }

        const firstPage = pageNumbers[0];
        const lastPage = pageNumbers[totalPages - 1];
        const currentPageIndex = currentPage - 1;

        if (currentPageIndex < 3) {
            return [firstPage, pageNumbers[1], pageNumbers[2], <span key="dots-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, lastPage];
        }

        if (currentPageIndex > totalPages - 4) {
            return [firstPage, <span key="dots-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, pageNumbers[totalPages - 3], pageNumbers[totalPages - 2], lastPage];
        }

        return [firstPage, <span key="dots-middle-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, pageNumbers[currentPageIndex - 1], pageNumbers[currentPageIndex], pageNumbers[currentPageIndex + 1], <span key="dots-middle-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, lastPage];
    };

    return (
        <div className="grid wide list-product" style={{ marginTop: 25 }}>
            <div className="row">
                {truyensOnPage.map(truyen => (
                    <div key={truyen.id} className="col l-2-4 m-3 c-4">
                        <div className="product">
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className="product-img">
                                    <img src={truyen.anhtruyen} alt={truyen.tentruyen} className="product-img" />
                                </div>
                                <h4 className="product-name">{truyen.tentruyen}</h4>
                                <div className="product-price">
                                    <span className="current-price"><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc && (
                                        <span className="original-price">
                                            <s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s>
                                        </span>
                                    )}
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>

            {totalPages > 1 && (
                <div className={categoryStyles.phantrang}>
                    {renderPageNumbers()}
                </div>
            )}
        </div>
    );
}

export default Category;
