import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Api_product = () => {
    const { id_truyen } = useParams(); // Lấy id_truyen từ URL
    const [truyen, setTruyen] = useState(null);
    // const [loading, setLoading] = useState(true);
    // const [error, setError] = useState(null);

    useEffect(() => {
        // Gọi API để lấy dữ liệu truyện
        axios.get(`http://localhost:3001/truyens/${id_truyen}`)
            .then(response => {
                console.log('Dữ liệu API:', response.data); // Kiểm tra dữ liệu
                setTruyen(response.data); // Lưu dữ liệu truyện vào state
                // setLoading(false); // Đặt trạng thái loading là false
            })
            .catch(() => {
                // setError('Có lỗi xảy ra'); // Xử lý lỗi khi gọi API
                // setLoading(false); // Đặt trạng thái loading là false
            });
    }, [id_truyen]);

    // Kiểm tra các trạng thái và trả về giao diện tương ứng
    // if (loading) return <div>Đang tải...</div>;
    // if (error) return <div>{error}</div>;
    if (!truyen) return <div>Không tìm thấy truyện</div>;

    return (
        <div>
            <h1>{truyen[0].tentruyen || 'Chưa có tên truyện'}</h1>
            <p><strong>Tác giả:</strong> {truyen[0].tacgia || 'Chưa có tác giả'}</p>
            <p><strong>Giá bán:</strong> {truyen[0].giaban ? `${truyen[0].giaban} VNĐ` : 'Chưa có giá bán'}</p>
            <p><strong>Giá gốc:</strong> {truyen[0].giagoc ? `${truyen[0].giagoc} VNĐ` : 'Chưa có giá gốc'}</p>
            <p><strong>ISBN:</strong> {truyen[0].isbn || 'Chưa có ISBN'}</p>
            <p><strong>Định dạng:</strong> {truyen[0].dinhdang || 'Chưa có định dạng'}</p>
            <p><strong>Đối tượng:</strong> {truyen[0].doituong || 'Chưa có đối tượng'}</p>
            <p><strong>Số lượng:</strong> {truyen[0].soluong || 'Chưa có số lượng'}</p>
            <p><strong>Số trang:</strong> {truyen[0].sotrang || 'Chưa có số trang'}</p>
            <p><strong>Trọng lượng:</strong> {truyen[0].trongluong || 'Chưa có trọng lượng'}</p>
            <p><strong>Ảnh:</strong>
                {truyen[0].anhtruyen ?
                    <img src={`path/to/images/${truyen[0].anhtruyen}`} alt={truyen[0].tentruyen} /> :
                    'Chưa có ảnh'}
            </p>
        </div>
    );
};

export default Api_product;
