import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';
import { decodeToken } from '../decodeToken';
import { useDiaChi } from './useDiaChi';
import { useGioHang } from './useGioHang';
import { useGiamGia as useMaGiamGia } from './useGiamGia';
import { funcGetTongTien, funcTongTienSauGG, taoID, convertNgayThangNam } from './utils';

const Payment = () => {
    // State cho phương thức thanh toán
    const [phuongThucTT, setPhuongThucTT] = useState('Thanh toán khi nhận hàng');
    // State cho địa chỉ cụ thể
    const [diaChiCT, setDiaChiCT] = useState('');
    // State cho mã khách hàng đặt hàng
    const [maKHDat, setMaKHDat] = useState(null);

    // Sử dụng custom hook quản lý địa chỉ
    const {
        dsTinh,
        dsHuyen,
        dsXa,
        maTinhChon,
        maHuyenChon,
        maXaChon,
        chonTinh,
        chonHuyen,
        chonXa,
        dangTaiTinh,
        loiTaiTinh,
    } = useDiaChi();

    // Sử dụng custom hook quản lý giỏ hàng với tên biến mới
    const { objGioHang, arrGioHang } = useGioHang();

    // Sử dụng custom hook quản lý giảm giá với tên biến và hàm mới
    const {
        inputMGG,
        maApDungThanhCong,
        phanTramGG,
        thongBaoGG,
        onChangeMGG,
        funcApDungMGG,
    } = useMaGiamGia();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decoded = decodeToken(token);
                setMaKHDat(decoded?.ma_nguoi_dung || null);
            } catch (error) {
                console.error('Lỗi giải mã token:', error);
                setMaKHDat(null);
            }
        } else {
            setMaKHDat(null);
        }
    }, []);

    const handlePaymentMethodChange = (event) => {
        setPhuongThucTT(event.target.value);
    };

    const layThongTinDonHang = () => {
        const tinhDaChon = dsTinh.find(tinh => tinh.code === parseInt(maTinhChon, 10));
        const huyenDaChon = dsHuyen.find(huyen => huyen.code === parseInt(maHuyenChon, 10));
        const xaDaChon = dsXa.find(xa => xa.code === parseInt(maXaChon, 10));

        const diaChiGiaoHang = `${diaChiCT}, ${xaDaChon?.name || ''}, ${huyenDaChon?.name || ''}, ${tinhDaChon?.name || ''}`;
        const maDonHang = taoID();
        const ngayDatHangFormatted = convertNgayThangNam(new Date());

        const donHang = {
            id: maDonHang,
            makhachhangdathang: maKHDat,
            manhanvienxulydonhang: null,
            ngaydathang: ngayDatHangFormatted,
            diachigiaohang: diaChiGiaoHang,
            trangthai: 'Chờ xử lý',
            phuongthucthanhtoan: phuongThucTT,
            magiamgia: maApDungThanhCong?.id || null, // Sử dụng maApDungThanhCong?.id
            tongtien: funcTongTienSauGG(funcGetTongTien(objGioHang), phanTramGG) // Sử dụng funcTongTienSauGG và funcGetTongTien
        };

        const chiTietDonHang = arrGioHang.map(item => ({
            id: taoID(),
            madonhang: maDonHang,
            matruyen: item.ID,
            soluong: item.SoLuongTruyen
        }));

        return { donHang, chiTietDonHang };
    };

    const handlePlaceOrder = async (event) => {
        event.preventDefault();
        const { donHang, chiTietDonHang } = layThongTinDonHang();

        try {
            const orderResponse = await axios.post('http://localhost:3001/donhangs/', donHang);
            console.log('Đơn hàng đã tạo:', donHang);

            for (const orderDetail of chiTietDonHang) {
                await axios.post('http://localhost:3001/chitietdonhangs/', orderDetail);
                console.log('Chi tiết đơn hàng đã tạo:', orderDetail);

                try {
                    const bookResponse = await axios.get(`http://localhost:3001/truyens/${orderDetail.matruyen}`);
                    const currentBook = bookResponse.data[0];
                    const remainingQuantity = currentBook.soluong - orderDetail.soluong;

                    if (remainingQuantity >= 0) {
                        await axios.put(`http://localhost:3001/truyens/${orderDetail.matruyen}`, { soluong: remainingQuantity });
                        console.log(`Đã cập nhật số lượng truyện ${orderDetail.matruyen} còn lại: ${remainingQuantity}`);
                    } else {
                        console.error(`Không đủ số lượng truyện ${orderDetail.matruyen}. Đặt: ${orderDetail.soluong}, Hiện có: ${currentBook.soluong}`);
                        alert(`Rất tiếc, số lượng truyện ${currentBook.tentruyen} không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return;
                    }
                } catch (error) {
                    console.error(`Lỗi lấy/cập nhật số lượng truyện ${orderDetail.matruyen}:`, error);
                    alert(`Lỗi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return;
                }
            }

            localStorage.removeItem('GioHang');
            alert('Đặt hàng thành công!');
            window.location.href = '/';
        } catch (error) {
            console.error('Lỗi tạo đơn hàng:', error);
            alert('Lỗi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {arrGioHang.length > 0 ? (
                            arrGioHang.map(item => (
                                <div className={styles['product-item']} key={item.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={item.AnhTruyen} alt={item.TenTruyen} />
                                        <span className={styles['soluongsp']}>{item.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{item.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{item.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm trong giỏ hàng.</p>
                        )}
                    </div>
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={inputMGG}
                            onChange={onChangeMGG}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={funcApDungMGG}>
                            Áp dụng
                        </button>
                    </div>
                    {thongBaoGG && (
                        <div className={`${maApDungThanhCong ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGG}
                        </div>
                    )}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{funcGetTongTien(objGioHang).toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGG > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{(funcGetTongTien(objGioHang) * phanTramGG / 100).toLocaleString('vi-VN')}đ ({phanTramGG}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{funcTongTienSauGG(funcGetTongTien(objGioHang), phanTramGG).toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handlePlaceOrder}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonTinh}
                        value={maTinhChon}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {dsTinh.map(province => (
                            <option key={province.code} value={province.code}>{province.name}</option>
                        ))}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonHuyen}
                        value={maHuyenChon}
                        disabled={!maTinhChon}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {dsHuyen.map(district => (
                            <option key={district.code} value={district.code}>{district.name}</option>
                        ))}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonXa}
                        value={maXaChon}
                        disabled={!maHuyenChon}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {dsXa.map(ward => (
                            <option key={ward.code} value={ward.code}>{ward.name}</option>
                        ))}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={diaChiCT}
                        onChange={(e) => setDiaChiCT(e.target.value)}
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={phuongThucTT === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={phuongThucTT === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {phuongThucTT === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
