import React, { useEffect, useRef } from "react";
import '../../component/grid.css';
import styles from './banner.module.css'; // Import module styles
import '../../component/style.css';
import './responsive.css';
import banner1 from '../img/banner_img1.jpg';
import banner2 from '../img/banner_img2.jpg';
import banner3 from '../img/banner_img3.jpg';
import banner4 from '../img/banner_img4.jpg';
import banner5 from '../img/banner_img5.jpg';

const Banner = () => {
    const listImgRef = useRef(null);
    const imgsRef = useRef([]);
    const btnLeftRef = useRef(null);
    const btnRightRef = useRef(null);
    const dotsRef = useRef([]);

    useEffect(() => {
        const listImg = listImgRef.current;
        const imgs = imgsRef.current;
        const btnLeft = btnLeftRef.current;
        const btnRight = btnRightRef.current;
        const dots = dotsRef.current;

        if (!listImg || imgs.length === 0 || !btnLeft || !btnRight || dots.length === 0) return;

        let current = 0;

        const updateSlider = () => {
            let width = imgs[0]?.offsetWidth || 0;
            listImg.style.transform = `translateX(${-width * current}px)`;
            const activeDot = dots.find(dot => dot?.classList?.contains('active'));
            if (activeDot) {
                activeDot.classList.remove('active');
            }
            dots[current]?.classList.add('active');
        };

        const toRight = () => {
            current = (current === imgs.length - 1) ? 0 : current + 1;
            updateSlider();
        };

        const toLeft = () => {
            current = (current === 0) ? imgs.length - 1 : current - 1;
            updateSlider();
        };

        let autoChange = setInterval(toRight, 4000);

        const handleRightClick = () => {
            clearInterval(autoChange);
            toRight();
            autoChange = setInterval(toRight, 4000);
        };

        const handleLeftClick = () => {
            clearInterval(autoChange);
            toLeft();
            autoChange = setInterval(toRight, 4000);
        };

        btnRight?.addEventListener('click', handleRightClick);
        btnLeft?.addEventListener('click', handleLeftClick);

        const dotClickHandlers = dots.map((dot, index) => {
            const handler = () => {
                current = index;
                updateSlider();
                clearInterval(autoChange);
                autoChange = setInterval(toRight, 4000);
            };
            dot?.addEventListener('click', handler);
            return { dot, handler };
        });

        return () => {
            clearInterval(autoChange);
            btnRight?.removeEventListener('click', handleRightClick);
            btnLeft?.removeEventListener('click', handleLeftClick);

            dotClickHandlers.forEach(({ dot, handler }) => {
                dot?.removeEventListener('click', handler);
            });
        };
    }, []);

    return (
        <div className={styles.banner}>
            <div className={styles['slide-show']}>
                <div id="img-banners" className="displayflex" ref={listImgRef} style={{ transition: 'transform 0.9s ease-in-out' }}>
                    {[banner1, banner2, banner3, banner4, banner5].map((banner, index) => (
                        <img
                            key={index}
                            className={styles['img-banner']}
                            src={banner}
                            ref={(el) => imgsRef.current[index] = el}
                        />
                    ))}
                </div>
            </div>
            <div className={styles['btns-banner']}>
                <div className={`${styles['btn-banner']} icon-white ${styles['btn-left']}`} ref={btnLeftRef}>
                    <i className="fas fa-chevron-left" />
                </div>
                <div className={`${styles['btn-banner']} icon-white ${styles['btn-right']}`} ref={btnRightRef}>
                    <i className="fas fa-chevron-right" />
                </div>
            </div>
            <div className={styles.dots}>
                {[...Array(5)].map((_, index) => (
                    <div
                        key={index}
                        className={`${styles.dot} ${index === current ? styles.active : ''}`}
                        ref={(el) => dotsRef.current[index] = el}
                        onClick={() => {
                            clearInterval(autoChange);
                            current = index;
                            updateSlider();
                            autoChange = setInterval(toRight, 4000);
                        }}
                    />
                ))}
            </div>
        </div>
    );
}

export default Banner;
