import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import styles from './home/home.module.css'; // Import CSS module cho trang chủ
import categoryStyles from './category/category.module.css'; // Import CSS module cho danh mục
import CheckScreenSize from './checkScreenSize'; // Component kiểm tra kích thước màn hình
import { editTenTruyen } from "./editTenTruyen"; // Hàm chỉnh sửa tên truyện

const SO_TRUYEN_MOI_TRANG = 10; // Số lượng truyện hiển thị trên mỗi trang

function TruyenTheoTheLoai(props) {
    // State lưu trữ danh sách truyện
    const [danhSachTruyen, setDanhSachTruyen] = useState([]);
    // State lưu trữ tên thể loại
    const [tenTheLoai, setTenTheLoai] = useState("");
    // State lưu trữ trang hiện tại
    const [trangHienTai, setTrangHienTai] = useState(1);
    // Ref để theo dõi các phần tử DOM cho việc kiểm tra kích thước màn hình
    const mobileRefs = useRef([]);

    // Gọi hook tùy chỉnh để kiểm tra kích thước màn hình và thêm/xóa class CSS
    CheckScreenSize(mobileRefs);

    // useEffect hook để tải dữ liệu khi component được mount hoặc khi matheloai thay đổi
    useEffect(function () {
        function fetchData() {
            axios.get("http://localhost:3001/truyens")
                .then(function (truyensRes) {
                    axios.get("http://localhost:3001/theloais")
                        .then(function (theloaiRes) {
                            var matheloai = props.matheloai;
                            var truyenTheoLoai = truyensRes.data.filter(function (item) {
                                return item.matheloai === matheloai;
                            });
                            setDanhSachTruyen(truyenTheoLoai);

                            var theLoaiTimThay = theloaiRes.data.find(function (tl) {
                                return String(tl.id).trim() === String(matheloai).trim();
                            });
                            if (theLoaiTimThay) {
                                setTenTheLoai(theLoaiTimThay.tentheloai);
                            }
                        })
                        .catch(function (error) {
                            console.error("Lỗi khi tải dữ liệu thể loại:", error);
                        });
                })
                .catch(function (error) {
                    console.error("Lỗi khi tải dữ liệu truyện:", error);
                });
        }
        fetchData();
    }, [props.matheloai]); // Chỉ chạy lại effect khi matheloai thay đổi

    // Tổng số truyện thuộc thể loại
    var tongSoTruyen = danhSachTruyen.length;
    // Tổng số trang dựa trên số lượng truyện mỗi trang
    var soTrang = Math.ceil(tongSoTruyen / SO_TRUYEN_MOI_TRANG);

    // Tính toán index bắt đầu và kết thúc của truyện trên trang hiện tại
    var indexBatDau = props.on_phantrang ? (trangHienTai - 1) * SO_TRUYEN_MOI_TRANG : 0;
    var indexKetThuc = props.on_phantrang ? Math.min(indexBatDau + SO_TRUYEN_MOI_TRANG, tongSoTruyen) : Math.min(props.soluong, tongSoTruyen);
    // Lấy ra danh sách truyện hiển thị trên trang hiện tại
    var truyenHienThi = danhSachTruyen.slice(indexBatDau, indexKetThuc);

    // Hàm thay đổi trang khi người dùng chọn trang khác
    function chuyenTrang(soTrangMoi) {
        setTrangHienTai(soTrangMoi);
    }

    // Hàm hiển thị các số trang để người dùng chuyển đổi
    function hienThiSoTrang() {
        if (!props.on_phantrang || soTrang <= 1) return null; // Không hiển thị phân trang nếu tắt hoặc chỉ có 1 trang
        var cacSoTrang = [];
        for (var i = 1; i <= soTrang; i++) {
            cacSoTrang.push(
                <div
                    key={i}
                    className={`${categoryStyles.trang} ${trangHienTai === i ? categoryStyles.trangchon : ''}`}
                    onClick={function () {
                        chuyenTrang(i);
                    }}
                >
                    <span><b>{i}</b></span>
                </div>
            );
        }
        return cacSoTrang;
    }

    // Nếu không có truyện nào thuộc thể loại, không hiển thị gì
    if (tongSoTruyen === 0) return null;

    return (
        <div className={`grid wide ${styles['list-product']}`}>
            <h2 style={{ margin: "10px auto", textTransform: 'uppercase' }}>{tenTheLoai}</h2>
            <div className={`row ${styles['list-item']}`}>
                {truyenHienThi.map(function (truyen, index) {
                    return (
                        <div
                            key={truyen.id}
                            className={`col l-2-4 m-3 c-4 checkSizeMobile ${styles['col']}`}
                            ref={function (el) {
                                mobileRefs.current[index] = el;
                            }}
                        >
                            <div className={styles['product']}>
                                <a href={`/truyen/${truyen.id}`} className="a-black">
                                    <div className={styles['product-img']}>
                                        <img src={truyen.anhtruyen} alt={truyen.tentruyen} className={styles['product-img']} />
                                    </div>
                                    <h4 className={styles['product-name']}>{editTenTruyen(truyen.tentruyen)}</h4>
                                    <div className={styles['product-price']}>
                                        <span className={styles['current-price']}><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                        {truyen.giagoc && <span className={styles['original-price']}><s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s></span>}
                                    </div>
                                </a>
                            </div>
                        </div>
                    );
                })}
            </div>
            {props.on_xemthem && (
                <div className="row">
                    <a className={`col l-12 m-12 c-12 a-red ${styles['xemthem']}`} href={`/danhmuc/${props.matheloai}`}>
                        <span>Xem thêm &gt;&gt;</span>
                    </a>
                </div>
            )}
            {props.on_phantrang && soTrang > 1 && (
                <div className={categoryStyles.phantrang}>
                    {hienThiSoTrang()}
                </div>
            )}
        </div>
    );
}

export default TruyenTheoTheLoai;
