import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import '../../component/grid.css';
import category from '../category/category.module.css';
import '../../component/style.css';
import '../home/home.module.css';
import TruyenTheoTheLoai from "../truyentheotheloai";
import updateCartCount from '../updateCartCount';

const SO_TRUYEN_MOI_TRANG = 12; // Số lượng truyện hiển thị trên mỗi trang

const Category = () => {
    useEffect(() => {
        updateCartCount();
    });

    const { id_theloai } = useParams();
    const [trangHienTai, setTrangHienTai] = useState(1); // State lưu trữ số trang hiện tại, mặc định là trang 1
    const [tongSoTruyen, setTongSoTruyen] = useState(0); // State lưu trữ tổng số truyện thuộc thể loại
    const [truyenTrenTrang, setTruyenTrenTrang] = useState([]); // State lưu trữ mảng truyện sẽ được hiển thị trên trang hiện tại


    useEffect(() => {
        const layTruyenTheoTheLoai = async () => {
            try {
                // Gọi API để lấy danh sách truyện theo mã thể loại
                const response = await fetch(`http://localhost:3001/truyens?matheloai=${id_theloai}`);
                const data = await response.json();
                setTongSoTruyen(data.length); // Cập nhật tổng số truyện

                // Tính toán index bắt đầu và kết thúc cho trang hiện tại
                const indexBatDau = (trangHienTai - 1) * SO_TRUYEN_MOI_TRANG;
                const indexKetThuc = indexBatDau + SO_TRUYEN_MOI_TRANG;
                const truyenChoTrang = data.slice(indexBatDau, indexKetThuc); // Lấy ra số truyện cần hiển thị cho trang hiện tại
                setTruyenTrenTrang(truyenChoTrang); // Cập nhật state truyện trên trang
            } catch (error) {
                console.error("Lỗi khi tải truyện theo thể loại:", error);
                setTongSoTruyen(0);
                setTruyenTrenTrang([]);
            }
        };

        layTruyenTheoTheLoai();
    }, [id_theloai, trangHienTai]); // Gọi lại effect khi mã thể loại hoặc trang hiện tại thay đổi


    return (
        <div className="grid wide list-product" style={{ marginTop: 25 }}>
            <TruyenTheoTheLoai matheloai={id_theloai} soluong={999} on_xemthem={false} />

            <div className={category.phantrang}>
                <div className={`${category.trang} ${category.trangchon}`} href="#">
                    <span>
                        <b>1</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>2</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>3</b>
                    </span>
                </div>
                <span className="flex-center" style={{ margin: "0px 20px" }}>
                    ...
                </span>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>8</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>9</b>
                    </span>
                </div>
            </div>
        </div>
    );
}

export default Category;
