import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import styles from './home/home.module.css';
import CheckScreenSize from './checkScreenSize';
import { editTenTruyen } from "./editTenTruyen";

const SO_TRUYEN_MOI_TRANG = 12; // Số lượng truyện hiển thị trên mỗi trang

const TruyenTheoTheLoai = ({ matheloai, soluongMacDinh = 5, on_xemthem = true }) => {
    const [truyens, setTruyens] = useState([]);
    const [tentheloai, setTenTheLoai] = useState("");
    const checkSizeMobileRefs = useRef([]);
    const [trangHienTai, setTrangHienTai] = useState(1);
    const [tongSoTruyen, setTongSoTruyen] = useState(0);

    CheckScreenSize(checkSizeMobileRefs);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [truyensRes, theloaiRes] = await Promise.all([
                    axios.get("http://localhost:3001/truyens"),
                    axios.get("http://localhost:3001/theloais")
                ]);

                // Lọc truyện theo mã thể loại
                const filtered = truyensRes.data.filter(item => item.matheloai === matheloai);
                setTongSoTruyen(filtered.length);
                setTruyens(filtered);

                // Tìm tên thể loại từ danh sách thể loại
                const theloai = theloaiRes.data.find(tl => String(tl.id).trim() === String(matheloai).trim());
                if (theloai) setTenTheLoai(theloai.tentheloai);
            } catch (error) {
                console.error("Lỗi khi tải dữ liệu:", error);
            }
        };

        fetchData();
        setTrangHienTai(1); // Reset trang về 1 khi matheloai thay đổi
    }, [matheloai]);

    const soTrang = Math.ceil(tongSoTruyen / SO_TRUYEN_MOI_TRANG);
    const indexBatDau = (trangHienTai - 1) * SO_TRUYEN_MOI_TRANG;
    const indexKetThuc = indexBatDau + SO_TRUYEN_MOI_TRANG;
    const truyenHienThi = truyens.slice(indexBatDau, indexKetThuc);

    const chuyenTrang = (soTrangMoi) => {
        setTrangHienTai(soTrangMoi);
    };

    const hienThiSoTrang = () => {
        const cacSoTrang = [];
        for (let i = 1; i <= soTrang; i++) {
            cacSoTrang.push(
                <div
                    key={i}
                    className={`${styles['trang-phan-trang']} ${trangHienTai === i ? styles['trang-phan-trang-chon'] : ''}`}
                    onClick={() => chuyenTrang(i)}
                >
                    <span>
                        <b>{i}</b>
                    </span>
                </div>
            );
        }

        if (soTrang <= 7) {
            return cacSoTrang;
        }

        const trangDau = cacSoTrang[0];
        const trangCuoi = cacSoTrang[soTrang - 1];
        const trangHienTaiIndex = trangHienTai - 1;

        if (trangHienTaiIndex < 3) {
            return [trangDau, cacSoTrang[1], cacSoTrang[2], <span key="dots-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, trangCuoi];
        }

        if (trangHienTaiIndex > soTrang - 4) {
            return [trangDau, <span key="dots-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, cacSoTrang[soTrang - 3], cacSoTrang[soTrang - 2], trangCuoi];
        }

        return [trangDau, <span key="dots-middle-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, cacSoTrang[trangHienTaiIndex - 1], cacSoTrang[trangHienTaiIndex], cacSoTrang[trangHienTaiIndex + 1], <span key="dots-middle-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, trangCuoi];
    };

    if (truyens.length === 0) return null;

    return (
        <div className={`grid wide ${styles['list-product']}`}>
            <h2 style={{ textTransform: 'uppercase' }}>{tentheloai}</h2>
            <div className={`row ${styles['list-item']}`}>
                {truyenHienThi.map((truyen, index) => (
                    <div
                        key={truyen.id}
                        className={`col l-2-4 m-3 c-4 checkSizeMobile ${styles['col']}`}
                        ref={(el) => (checkSizeMobileRefs.current[index] = el)}
                    >
                        <div className={styles['product']}>
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className={styles['product-img']}>
                                    <img src={truyen.anhtruyen} alt={truyen.tentruyen} className={styles['product-img']} />
                                </div>
                                <h4 className={styles['product-name']}>{editTenTruyen(truyen.tentruyen)}</h4>
                                <div className={styles['product-price']}>
                                    <span className={styles['current-price']}><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc && (
                                        <span className={styles['original-price']}>
                                            <s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s>
                                        </span>
                                    )}
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>
            {soTrang > 1 && (
                <div className={styles['phan-trang']}>
                    {hienThiSoTrang()}
                </div>
            )}
            {on_xemthem && (
                <div className="row">
                    <a className={`col l-12 m-12 c-12 a-red ${styles['xemthem']}`} href={`/danhmuc/${matheloai}`}>
                        <span>Xem thêm &gt;&gt;</span>
                    </a>
                </div>
            )}
        </div>
    );
};

export default TruyenTheoTheLoai;
