import React, { useEffect } from "react";
import '../../component/grid.css';
import homeStyles from '../home/home.module.css';
import '../../component/style.css';
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import useTruyenTheoQuangCaoData from "../truyentheoquangcao";
import renderTruyen from "../renderTruyen";

const Home = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    const maQuangCao1 = 'QC001';
    const maQuangCao2 = 'QC002';
    const maQuangCao3 = 'QC003';
    const maQuangCao4 = 'lb7ssmtf';
    const maQuangCao5 = 'lbmcb1jd';
    const maQuangCao6 = 'lbwst2lr';
    const maQuangCao7 = 'lc4ymoui';
    const maQuangCao8 = 'lcf1sre5';
    const maQuangCao9 = 'lcr3g6f2';


    const qc1Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao1 });
    const qc2Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao2 });
    const qc3Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao3 });
    const qc4Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao4 });
    const qc5Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao5 });
    const qc6Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao6 });
    const qc7Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao7 });
    const qc8Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao8 });
    const qc9Data = useTruyenTheoQuangCaoData({ maquangcao: maQuangCao9 });


    return (
        //         <>
        //             {/* <TruyenTheoQuangCao maquangcao="QC001" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="QC002" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="QC003" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_1} />
        //             <TruyenTheoQuangCao maquangcao="lb7ssmtf" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_2} />
        //             <TruyenTheoQuangCao maquangcao="lbmcb1jd" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_3} />
        //             <TruyenTheoQuangCao maquangcao="lbwst2lr" soluong={5} />
        //             <img className={homeStyles['img-title']} src={banner_home_4} />
        //             <TruyenTheoQuangCao maquangcao="lc4ymoui" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="lcf1sre5" soluong={5} />
        //             <TruyenTheoQuangCao maquangcao="lcr3g6f2" soluong={5} />
        //  */}
        //         </>

        <>
            {qc1Data.loading ? <p>Đang tải {qc1Data.tenquangcao || maQuangCao1}...</p> : qc1Data.error ? <p>Lỗi tải {qc1Data.tenquangcao || maQuangCao1}: {qc1Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc1Data.truyens.slice(0, 5)}
                    tieude={qc1Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {qc2Data.loading ? <p>Đang tải {qc2Data.tenquangcao || maQuangCao2}...</p> : qc2Data.error ? <p>Lỗi tải {qc2Data.tenquangcao || maQuangCao2}: {qc2Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc2Data.truyens.slice(0, 5)}
                    tieude={qc2Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {qc3Data.loading ? <p>Đang tải {qc3Data.tenquangcao || maQuangCao3}...</p> : qc3Data.error ? <p>Lỗi tải {qc3Data.tenquangcao || maQuangCao3}: {qc3Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc3Data.truyens.slice(0, 5)}
                    tieude={qc3Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_1} alt="Banner 1" />
            {qc4Data.loading ? <p>Đang tải {qc4Data.tenquangcao || maQuangCao4}...</p> : qc4Data.error ? <p>Lỗi tải {qc4Data.tenquangcao || maQuangCao4}: {qc4Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc4Data.truyens.slice(0, 5)}
                    tieude={qc4Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_2} alt="Banner 2" />
            {qc5Data.loading ? <p>Đang tải {qc5Data.tenquangcao || maQuangCao5}...</p> : qc5Data.error ? <p>Lỗi tải {qc5Data.tenquangcao || maQuangCao5}: {qc5Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc5Data.truyens.slice(0, 5)}
                    tieude={qc5Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_3} alt="Banner 3" />
            {qc6Data.loading ? <p>Đang tải {qc6Data.tenquangcao || maQuangCao6}...</p> : qc6Data.error ? <p>Lỗi tải {qc6Data.tenquangcao || maQuangCao6}: {qc6Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc6Data.truyens.slice(0, 5)}
                    tieude={qc6Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            <img className={homeStyles['img-title']} src={banner_home_4} alt="Banner 4" />
            {qc7Data.loading ? <p>Đang tải {qc7Data.tenquangcao || maQuangCao7}...</p> : qc7Data.error ? <p>Lỗi tải {qc7Data.tenquangcao || maQuangCao7}: {qc7Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc7Data.truyens.slice(0, 5)}
                    tieude={qc7Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {qc8Data.loading ? <p>Đang tải {qc8Data.tenquangcao || maQuangCao8}...</p> : qc8Data.error ? <p>Lỗi tải {qc8Data.tenquangcao || maQuangCao8}: {qc8Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc8Data.truyens.slice(0, 5)}
                    tieude={qc8Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
            {qc9Data.loading ? <p>Đang tải {qc9Data.tenquangcao || maQuangCao9}...</p> : qc9Data.error ? <p>Lỗi tải {qc9Data.tenquangcao || maQuangCao9}: {qc9Data.error.message}</p> : (
                <DanhSachTruyen
                    truyens={qc9Data.truyens.slice(0, 5)}
                    tieude={qc9Data.tenquangcao}
                    on_xemthem={false}
                    on_phantrang={false}
                />
            )}
        </>
    )
}

export default Home;
