import React, { useState } from 'react';
import styles from './login.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleTogglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleLogin = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password })
            });

            if (response.ok) {
                const data = await response.json();
                localStorage.setItem('token', data.token);
                navigate('/');
                // Nếu bạn có logic cập nhật trạng thái đăng nhập toàn cục, hãy thêm vào đây
            } else {
                const errorData = await response.text();
                setErrorMessage(errorData || 'Đăng nhập thất bại. Vui lòng kiểm tra lại thông tin đăng nhập.');
            }
        } catch (error) {
            setErrorMessage('Đã xảy ra lỗi khi kết nối đến máy chủ.');
            console.error('Lỗi đăng nhập:', error);
        }
    };

    return (
        <div className="grid wide">
            <div id="main" className="row flex-center">
                <form id="main-form" onSubmit={handleLogin}>
                    <h1 className="flex-center">ĐĂNG NHẬP</h1>
                    {errorMessage && <p className={styles.errorMessage}>{errorMessage}</p>}
                    <input
                        id="taikhoan"
                        className={`${styles.textbox}`}
                        type="text"
                        placeholder="Tài khoản"
                        required=""
                        value={username}
                        onChange={handleUsernameChange}
                    />
                    <div className={styles['main-input']}>
                        <input
                            id="matkhau"
                            className={`${styles.textbox}`}
                            type={showPassword ? 'text' : 'password'}
                            placeholder="Mật khẩu"
                            required=""
                            value={password}
                            onChange={handlePasswordChange}
                        />
                        <span className={styles['icon-input']} onClick={handleTogglePasswordVisibility} style={{ cursor: 'pointer' }}>
                            <i className={`fas ${showPassword ? 'fa-eye' : 'fa-eye-slash'}`} />
                        </span>
                    </div>
                    <button id="dangnhap" type="submit" className={`${styles.textbox} ${styles.dangnhap}`}>
                        <b>Đăng nhập</b>
                    </button>
                    <p className={`chucnang flex-center ${styles.chucnang}`}>
                        <a className="a-red" href="/dangky">
                            Đăng ký
                        </a>
                    </p>
                    <p className={`chucnang flex-center ${styles.chucnang}`}>
                        <a className="a-red" href="/quenmatkhau">
                            Quên mật khẩu?
                        </a>
                    </p>
                </form>
            </div>
        </div>
    );
};

export default Login;
