function updateCartCount(cartCountRefPC, cartCountRefMobile) {
    const GioHang = localStorage.getItem('GioHang');
    let gioHang_json = {};
    if (GioHang !== null) {
        gioHang_json = JSON.parse(GioHang);
    }

    const arrGioHang = Object.values(gioHang_json);

    let soluong = 0;

    for (let i = 0; i < arrGioHang.length; i++) {
        const sanPham = arrGioHang[i];
        let sl = sanPham.SoLuongTruyen;

        soluong = soluong + sl;
    }

    let giaTriHienThi;
    if (soluong < 10) {
        giaTriHienThi = soluong;
    } else {
        giaTriHienThi = "9+";
    }

    if (cartCountRefPC !== null && cartCountRefPC.current !== null) {
        cartCountRefPC.current.textContent = giaTriHienThi;
    }

    if (cartCountRefMobile !== null && cartCountRefMobile.current !== null) {
        cartCountRefMobile.current.textContent = giaTriHienThi;
    }
}

export default updateCartCount;
