function updateCartCount(cartCountRefPC, cartCountRefMobile) {
    const gioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
    arrGioHang = Object.values(gioHang);

    const totalQuantity = Object.values(gioHang).reduce(
        (acc, item) => acc + (item.SoLuongTruyen || 0),
        0
    );
    const displayCount = totalQuantity < 10 ? totalQuantity : '9+';

    const updateRef = (ref) => {
        if (ref?.current) {
            ref.current.textContent = displayCount;
        }
    };

    updateRef(cartCountRefPC);
    updateRef(cartCountRefMobile);
}

export default updateCartCount;
