import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../shoppingcart/shoppingcart.css';
import '../../component/style.css';
import '../home/responsive.css';

function Shoppingcart() {
    const [cart, setCart] = useState({});

    useEffect(() => {
        const storedCart = JSON.parse(localStorage.getItem('GioHang')) || {};
        setCart(storedCart);
    }, []);

    function updateCart(newCart) {
        setCart(newCart);
        localStorage.setItem('GioHang', JSON.stringify(newCart));
    }

    function updateQuantity(productName, quantity) {
        if (quantity <= 0) return;

        const newCart = { ...cart };
        if (newCart[productName]) {
            newCart[productName].SoLuongTruyen = quantity;
            newCart[productName].TongTien = quantity * newCart[productName].GiaBanTruyen;
        }
        updateCart(newCart);
    }

    function removeFromCart(productName) {
        const newCart = { ...cart };
        delete newCart[productName];
        updateCart(newCart);
    }

    function getTamTinh() {
        return Object.values(cart).reduce((sum, item) => sum + item.TongTien, 0);
    }

    return (
        <div className="grid wide">
            <div className="row">
                <table className="tableCart">
                    <thead className="tieude">
                        <tr>
                            <th colSpan={2} className="text-center">Sản phẩm</th>
                            <th className="text-center">Đơn giá</th>
                            <th className="text-center">Số lượng</th>
                            <th className="text-center">Tổng giá</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(cart).map((item) => (
                            <tr key={item} className="cart-product">
                                <td><img className="cart-product-img" src={cart[item].AnhTruyen} alt={item} /></td>
                                <td>
                                    <h2>{cart[item].TenTruyen}</h2>
                                    <button className="cart-product-remove" onClick={() => removeFromCart(item)}>Xóa</button>
                                </td>
                                <td className="text-center">{cart[item].GiaBanTruyen.toLocaleString('vi-VN')}đ</td>
                                <td>
                                    <input type="number" min="1" value={cart[item].SoLuongTruyen}
                                        onChange={(e) => updateQuantity(item, parseInt(e.target.value))} />
                                </td>
                                <td className="text-center">{cart[item].TongTien.toLocaleString('vi-VN')}đ</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="row">
                <div className="col text-right">
                    <p>Tạm tính: <b>{getTamTinh().toLocaleString('vi-VN')}đ</b></p>
                </div>
                <div className="col text-right">
                    <button id="thanhtoan" type="submit"><b>Thanh toán</b></button>
                </div>
            </div>
        </div>
    );
}
export default Shoppingcart;
