import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../shoppingcart/shoppingcart.css';
import '../../component/style.css';
import '../home/responsive.css';

function GioHang() {
    // State lưu trữ giỏ hàng
    const [gioHang, setGioHang] = useState({});
    const [soLuongGioHang, setSoLuongGioHang] = useState(0);

    // Lấy dữ liệu giỏ hàng từ localStorage khi component được render
    useEffect(() => {
        const duLieuGioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        setGioHang(duLieuGioHang);
        capNhatSoLuongGioHang(duLieuGioHang);
    }, []);

    // Cập nhật giỏ hàng và lưu vào localStorage
    function capNhatGioHang(gioHangMoi) {
        setGioHang(gioHangMoi);
        localStorage.setItem('GioHang', JSON.stringify(gioHangMoi));
        capNhatSoLuongGioHang(gioHangMoi);
    }

    // Cập nhật số lượng hiển thị giỏ hàng
    function capNhatSoLuongGioHang(gioHangHienTai) {
        let tongSoLuong = Object.values(gioHangHienTai).reduce((tong, sp) => tong + sp.SoLuongTruyen, 0);
        setSoLuongGioHang(tongSoLuong);
    }

    // Cập nhật số lượng sản phẩm
    function capNhatSoLuong(tenSanPham, soLuong) {
        if (soLuong <= 0) return;

        const gioHangMoi = { ...gioHang };
        if (gioHangMoi[tenSanPham]) {
            gioHangMoi[tenSanPham].SoLuongTruyen = soLuong;
            gioHangMoi[tenSanPham].TongTien = soLuong * gioHangMoi[tenSanPham].GiaBanTruyen;
        }
        capNhatGioHang(gioHangMoi);
    }

    // Xóa sản phẩm khỏi giỏ hàng
    function xoaKhoiGioHang(tenSanPham) {
        const gioHangMoi = { ...gioHang };
        delete gioHangMoi[tenSanPham];
        capNhatGioHang(gioHangMoi);
    }

    // Tính tổng tiền tạm tính
    function tinhTongTamTinh() {
        return Object.values(gioHang).reduce((tong, sanPham) => tong + sanPham.TongTien, 0);
    }

    return (
        <div className="grid wide">
            <div className="row">
                <table className="tableCart">
                    <thead className="tieude">
                        <tr>
                            <th colSpan={2} className="text-center">Sản phẩm</th>
                            <th className="text-center">Đơn giá</th>
                            <th className="text-center">Số lượng</th>
                            <th className="text-center">Tổng giá</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(gioHang).map((tenSanPham) => (
                            <tr key={tenSanPham} className="cart-product">
                                <td>
                                    <img className="cart-product-img" src={gioHang[tenSanPham].AnhTruyen} alt={tenSanPham} />
                                </td>
                                <td>
                                    <h3>{gioHang[tenSanPham].TenTruyen}</h3>
                                    <a className="cart-product-remove" onClick={() => xoaKhoiGioHang(tenSanPham)}>Xóa</a>
                                </td>
                                <td className="text-center">
                                    <b>{gioHang[tenSanPham].GiaBanTruyen.toLocaleString('vi-VN')}đ</b>
                                </td>
                                <td>
                                    <div className="ArrowInputTypeNumber">
                                        <button className="button-down flex-center" onClick={() => capNhatSoLuong(tenSanPham, gioHang[tenSanPham].SoLuongTruyen - 1)}>
                                            <i className="fas fa-chevron-down"></i>
                                        </button>
                                        <input
                                            type="text"
                                            className="input-soluong"
                                            value={gioHang[tenSanPham].SoLuongTruyen}
                                            onChange={(e) => capNhatSoLuong(tenSanPham, parseInt(e.target.value) || 1)}
                                        />
                                        <button className="button-up flex-center" onClick={() => capNhatSoLuong(tenSanPham, gioHang[tenSanPham].SoLuongTruyen + 1)}>
                                            <i className="fas fa-chevron-up"></i>
                                        </button>
                                    </div>
                                </td>
                                <td className="text-center">
                                    <b>{gioHang[tenSanPham].TongTien.toLocaleString('vi-VN')}đ</b>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="row">
                <div className="col c-o-4 c-8 m-o-8 m-4 l-o-9 l-3" style={{ display: "inline-flex", lineHeight: 3, justifyContent: "flex-end" }}>
                    <p className="flex-center">Tạm tính: &nbsp;</p>
                    <b>
                        <p className="tamtinh">{tinhTongTamTinh().toLocaleString('vi-VN')}đ</p>
                    </b>
                </div>
                <div className="col c-o-8 c-4 m-o-8 m-4 l-o-9 l-3">
                    <button id="thanhtoan" type="submit">
                        <b>Thanh toán</b>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default GioHang;
