import React, { useEffect, useState } from "react";
import axios from "axios";
import './grid.css';
import './style.css';
import styles from './home/home.module.css';
// import responsiveStyles from './home/home.module.css';


const TruyenTheoQuangCao = ({ maquangcao, soluong = 5, on_xemthem = true }) => {
    const [truyens, setTruyens] = useState([]);
    const [tenquangcao, setTenQuangCao] = useState("");
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        function checkScreenSize() {
            const checkSizeMobile = document.querySelectorAll('.checkSizeMobile');
            checkSizeMobile.forEach(item => {
                if (window.innerWidth > 520) {
                    item.classList.remove('c-6');
                    item.classList.add('c-4');
                } else {
                    item.classList.remove('c-4');
                    item.classList.add('c-6');
                }
            });
        }

        // Gọi function ngay khi component render
        checkScreenSize();

        // Lắng nghe sự kiện resize
        window.addEventListener('resize', checkScreenSize);

        const fetchData = async () => {
            try {
                const [truyensRes, quangcaoRes] = await Promise.all([
                    axios.get("http://localhost:3001/truyens"),
                    axios.get("http://localhost:3001/quangcaos")
                ]);

                // Lọc truyện theo mã quảng cáo
                const filtered = truyensRes.data.filter(item => item.maquangcao === maquangcao);
                setTruyens(filtered);

                // Tìm tên quảng cáo từ danh sách quảng cáo
                const quangcao = quangcaoRes.data.find(qc => String(qc.id).trim() === String(maquangcao).trim());
                if (quangcao) setTenQuangCao(quangcao.tenquangcao);
            } catch (error) {
                console.error("Lỗi khi tải dữ liệu:", error);
            } finally {
                setLoading(false);
            }
        };
        return () => {
            window.removeEventListener('resize', checkScreenSize);
        };

        fetchData();
    }, [maquangcao]);

    if (loading) return <p>Đang tải...</p>;
    if (truyens.length === 0) return null;

    return (
        <div className={`grid wide ${styles['list-product']}`}>
            <h2 style={{ margin: "10px auto", textTransform: 'uppercase' }}>{tenquangcao}</h2>
            <div className={`row ${styles['list-item']}`}>
                {truyens.slice(0, soluong).map(truyen => (
                    <div key={truyen.id} className={`col l-2-4 m-3 c-4 checkSizeMobile ${styles['col']}`}>
                        <div className={styles['product']}>
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className={styles['product-img']}>
                                    <img src={truyen.anhtruyen} alt={truyen.tentruyen} className={styles['product-img']} />
                                </div>
                                <h4 className={styles['product-name']}>{truyen.tentruyen}</h4>
                                <div className={styles['product-price']}>
                                    <span className={styles['current-price']}><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc && (
                                        <span className={styles['original-price']}>
                                            <s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s>
                                        </span>
                                    )}
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>
            {on_xemthem && (
                <div className="row">
                    <a className={`col l-12 m-12 c-12 a-red ${styles['xemthem']}`} href={`/quangcao/${maquangcao}`}>
                        <span>Xem thêm &gt;&gt;</span>
                    </a>
                </div>
            )}
        </div>
    );
};

export default TruyenTheoQuangCao;
