import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';
import { decodeToken } from '../decodeToken';
import { useDiaChi } from './useDiaChi';
import { useGioHang } from './useGioHang';
import { useGiamGia as useMaGiamGia } from './useGiamGia';
import { funcGetTongTien, funcTongTienSauGG, taoID, convertNgayThangNam } from './utils';

const Payment = () => {
    // Trạng thái cho phương thức thanh toán (mặc định là thanh toán khi nhận hàng)
    const [phuongThucThanhToan, setPhuongThucThanhToan] = useState('Thanh toán khi nhận hàng');
    // Trạng thái cho địa chỉ chi tiết người dùng nhập
    const [diaChiChiTiet, setDiaChiChiTiet] = useState('');
    // Trạng thái cho mã khách hàng đặt hàng (lấy từ token)
    const [maKhachHangDatHang, setMaKhachHangDatHang] = useState(null);

    // Sử dụng custom hook quản lý địa chỉ (tỉnh, huyện, xã)
    const {
        danhSachTinh,
        danhSachHuyen,
        danhSachXa,
        maTinhDaChon,
        maHuyenDaChon,
        maXaDaChon,
        chonTinh,
        chonHuyen,
        chonXa,
        dangTaiTinh,
        loiTaiTinh,
    } = useDiaChi();

    // Sử dụng custom hook quản lý giỏ hàng
    const { objGioHang, arrGioHang } = useGioHang();

    // Sử dụng custom hook quản lý giảm giá (mã giảm giá)
    const {
        maGiamGiaNhap,
        maGiamGiaDaApDung,
        phanTramGiamGia,
        thongBaoGiamGia,
        onChangeMaGiamGia,
        apDungMaGiamGia,
    } = useMaGiamGia();

    // useEffect chạy khi component mount hoặc khi token thay đổi
    useEffect(() => {
        // Lấy token từ localStorage
        const token = localStorage.getItem('token');
        // Nếu có token
        if (token) {
            try {
                // Giải mã token để lấy thông tin người dùng
                const thongTinToken = decodeToken(token);
                // Lấy mã người dùng từ thông tin giải mã được (nếu có)
                setMaKhachHangDatHang(thongTinToken?.ma_nguoi_dung || null);
            } catch (error) {
                // Nếu có lỗi giải mã token, in lỗi và đặt mã khách hàng là null
                console.error('Lỗi giải mã token:', error);
                setMaKhachHangDatHang(null);
            }
        } else {
            // Nếu không có token, đặt mã khách hàng là null
            setMaKhachHangDatHang(null);
        }
    }, []); // [] nghĩa là useEffect chỉ chạy một lần sau khi component render lần đầu

    // Hàm xử lý khi phương thức thanh toán thay đổi
    const handleThayDoiPhuongThucTT = (event) => {
        setPhuongThucThanhToan(event.target.value);
    };

    // Hàm lấy thông tin đơn hàng để gửi lên server
    const layThongTinDonHang = () => {
        // Tìm tỉnh đã chọn trong danh sách tỉnh
        let tinhDaChon = null;
        for (let i = 0; i < danhSachTinh.length; i++) {
            if (danhSachTinh[i].code === parseInt(maTinhDaChon, 10)) {
                tinhDaChon = danhSachTinh[i];
                break;
            }
        }

        // Tìm huyện đã chọn trong danh sách huyện
        let huyenDaChon = null;
        for (let i = 0; i < danhSachHuyen.length; i++) {
            if (danhSachHuyen[i].code === parseInt(maHuyenDaChon, 10)) {
                huyenDaChon = danhSachHuyen[i];
                break;
            }
        }

        // Tìm xã đã chọn trong danh sách xã
        let xaDaChon = null;
        for (let i = 0; i < danhSachXa.length; i++) {
            if (danhSachXa[i].code === parseInt(maXaDaChon, 10)) {
                xaDaChon = danhSachXa[i];
                break;
            }
        }

        // Tạo địa chỉ giao hàng đầy đủ
        const diaChiGiaoHang = diaChiChiTiet + ', ' + (xaDaChon ? xaDaChon.name : '') + ', ' + (huyenDaChon ? huyenDaChon.name : '') + ', ' + (tinhDaChon ? tinhDaChon.name : '');
        // Tạo mã đơn hàng ngẫu nhiên
        const maDonHang = taoID();
        // Lấy ngày đặt hàng hiện tại và định dạng theo YYYY-MM-DD
        const ngayDatHang = convertNgayThangNam(new Date());

        // Đối tượng chứa thông tin đơn hàng
        const thongTinDonHang = {
            id: maDonHang,
            makhachhangdathang: maKhachHangDatHang,
            manhanvienxulydonhang: null,
            ngaydathang: ngayDatHang,
            diachigiaohang: diaChiGiaoHang,
            trangthai: 'Chờ xử lý',
            phuongthucthanhtoan: phuongThucThanhToan,
            magiamgia: maGiamGiaDaApDung ? maGiamGiaDaApDung.id : null,
            tongtien: funcTongTienSauGG(funcGetTongTien(objGioHang), phanTramGiamGia)
        };

        // Mảng chứa thông tin chi tiết đơn hàng (từng sản phẩm trong giỏ hàng)
        const chiTietDonHang = [];
        for (let i = 0; i < arrGioHang.length; i++) {
            const sanPham = arrGioHang[i];
            chiTietDonHang.push({
                id: taoID(),
                madonhang: maDonHang,
                matruyen: sanPham.ID,
                soluong: sanPham.SoLuongTruyen
            });
        }

        // Trả về thông tin đơn hàng và chi tiết đơn hàng
        return { thongTinDonHang, chiTietDonHang };
    };

    // Hàm xử lý khi người dùng bấm nút đặt hàng
    const handleDatHang = async (event) => {
        // Ngăn chặn hành vi mặc định của form (reload trang)
        event.preventDefault();
        // Lấy thông tin đơn hàng và chi tiết đơn hàng
        const { thongTinDonHang, chiTietDonHang } = layThongTinDonHang();

        try {
            // Gửi yêu cầu POST lên server để tạo đơn hàng
            const responseDonHang = await axios.post('http://localhost:3001/donhangs/', thongTinDonHang);
            console.log('Đơn hàng đã tạo:', thongTinDonHang);

            // Lặp qua từng chi tiết đơn hàng để gửi lên server và cập nhật số lượng sách
            for (let i = 0; i < chiTietDonHang.length; i++) {
                const itemChiTiet = chiTietDonHang[i];
                // Gửi yêu cầu POST lên server để tạo chi tiết đơn hàng
                await axios.post('http://localhost:3001/chitietdonhangs/', itemChiTiet);
                console.log('Chi tiết đơn hàng đã tạo:', itemChiTiet);

                try {
                    // Lấy thông tin sách từ server để kiểm tra số lượng
                    const responseSach = await axios.get(`http://localhost:3001/truyens/${itemChiTiet.matruyen}`);
                    const thongTinSach = responseSach.data[0];
                    const soLuongConLai = thongTinSach.soluong - itemChiTiet.soluong;

                    // Nếu số lượng còn lại không âm (đủ sách)
                    if (soLuongConLai >= 0) {
                        // Cập nhật số lượng sách trên server
                        await axios.put(`http://localhost:3001/truyens/${itemChiTiet.matruyen}`, { soluong: soLuongConLai });
                        console.log(`Đã cập nhật số lượng truyện ${itemChiTiet.matruyen} còn lại: ${soLuongConLai}`);
                    } else {
                        // Nếu không đủ sách, báo lỗi và dừng đặt hàng
                        console.error(`Không đủ số lượng truyện ${itemChiTiet.matruyen}. Đặt: ${itemChiTiet.soluong}, Hiện có: ${thongTinSach.soluong}`);
                        alert(`Rất tiếc, số lượng truyện ${thongTinSach.tentruyen} không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return;
                    }
                } catch (error) {
                    // Nếu có lỗi khi lấy hoặc cập nhật số lượng sách
                    console.error(`Lỗi lấy/cập nhật số lượng truyện ${itemChiTiet.matruyen}:`, error);
                    alert(`Lỗi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return;
                }
            }

            // Sau khi đặt hàng thành công, xóa giỏ hàng khỏi localStorage
            localStorage.removeItem('GioHang');
            alert('Đặt hàng thành công!');
            // Chuyển hướng người dùng về trang chủ
            window.location.href = '/';
        } catch (error) {
            // Nếu có lỗi trong quá trình tạo đơn hàng
            console.error('Lỗi tạo đơn hàng:', error);
            alert('Lỗi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {arrGioHang.length > 0 ? (
                            arrGioHang.map(function (sanPham) {
                                return (
                                    <div className={styles['product-item']} key={sanPham.ID}>
                                        <div className={styles['product-img']}>
                                            <img src={sanPham.AnhTruyen} alt={sanPham.TenTruyen} />
                                            <span className={styles['soluongsp']}>{sanPham.SoLuongTruyen}</span>
                                        </div>
                                        <div className={styles['product-info']}>
                                            <h5 className={styles['product-name']}>{sanPham.TenTruyen}</h5>
                                            <p className={styles['product-price']}>{sanPham.GiaBanTruyen ? sanPham.GiaBanTruyen.toLocaleString('vi-VN') : 0}đ</p>
                                        </div>
                                    </div>
                                );
                            })
                        ) : (
                            <p>Không có sản phẩm trong giỏ hàng.</p>
                        )}
                    </div>
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaNhap}
                            onChange={onChangeMaGiamGia}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={apDungMaGiamGia}>
                            Áp dụng
                        </button>
                    </div>
                    {thongBaoGiamGia && (
                        <div className={`${maGiamGiaDaApDung ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{funcGetTongTien(objGioHang).toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{(funcGetTongTien(objGioHang) * phanTramGiamGia / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{funcTongTienSauGG(funcGetTongTien(objGioHang), phanTramGiamGia).toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handleDatHang}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonTinh}
                        value={maTinhDaChon}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {danhSachTinh.map(function (tinh) {
                            return <option key={tinh.code} value={tinh.code}>{tinh.name}</option>;
                        })}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonHuyen}
                        value={maHuyenDaChon}
                        disabled={!maTinhDaChon}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {danhSachHuyen.map(function (huyen) {
                            return <option key={huyen.code} value={huyen.code}>{huyen.name}</option>;
                        })}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={chonXa}
                        value={maXaDaChon}
                        disabled={!maHuyenDaChon}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {danhSachXa.map(function (xa) {
                            return <option key={xa.code} value={xa.code}>{xa.name}</option>;
                        })}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={diaChiChiTiet}
                        onChange={function (e) { setDiaChiChiTiet(e.target.value); }}
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={phuongThucThanhToan === 'Thanh toán khi nhận hàng'}
                                onChange={handleThayDoiPhuongThucTT}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={phuongThucThanhToan === 'Chuyển khoản ngân hàng'}
                                onChange={handleThayDoiPhuongThucTT}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {phuongThucThanhToan === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};
