import React, { useState, useEffect } from 'react';
import './payment.module.css'; // Import CSS cho trang này
import '../../component/grid.css'; // Import CSS lưới (grid)
import '../../component/style.css'; // Import CSS chung
import qrpay from '../img/qr-pay.jpg'; // Hình ảnh QR code thanh toán
import axios from 'axios'; // Thư viện để gọi API
import { decodeToken } from '../decodeToken'; // Hàm giải mã token (nếu bạn dùng xác thực)
import { useDiaChi } from './useDiaChi'; // Hook tùy chỉnh quản lý địa chỉ
import { useGioHang } from './useGioHang'; // Hook tùy chỉnh quản lý giỏ hàng
import { useGiamGia as useMaGiamGia } from './useGiamGia'; // Hook tùy chỉnh quản lý mã giảm giá
import {
    tinhTongTienHang, // Hàm tính tổng tiền hàng
    tinhTongTienSauGiamGia, // Hàm tính tổng tiền sau giảm giá
    taoID, // Hàm tạo ID duy nhất
    formatDateForDatabase // Hàm định dạng ngày tháng cho database
} from './utils';

const Payment = () => {
    // State (biến trạng thái) cho phương thức thanh toán
    const [phuongThucThanhToan, setPhuongThucThanhToan] = useState('Thanh toán khi nhận hàng');

    // State cho địa chỉ cụ thể (số nhà, tên đường...)
    const [diaChiCuThe, setDiaChiCuThe] = useState('');

    // State cho mã khách hàng đặt hàng
    const [maKhachHang, setMaKhachHang] = useState(null);

    // Sử dụng hook tùy chỉnh quản lý địa chỉ
    const {
        danhSachTinhThanh, // Danh sách các tỉnh thành
        danhSachQuanHuyen, // Danh sách các quận huyện thuộc tỉnh đã chọn
        danhSachPhuongXa, // Danh sách các phường xã thuộc quận huyện đã chọn
        maTinhThanhDaChon, // Mã của tỉnh thành đã chọn
        maQuanHuyenDaChon, // Mã của quận huyện đã chọn
        maPhuongXaDaChon, // Mã của phường xã đã chọn
        xuLyThayDoiTinhThanh, // Hàm xử lý khi chọn tỉnh thành
        xuLyThayDoiQuanHuyen, // Hàm xử lý khi chọn quận huyện
        xuLyThayDoiPhuongXa, // Hàm xử lý khi chọn phường xã
        dangTaiTinhThanh, // Biến cho biết đang tải danh sách tỉnh thành
        loiTaiTinhThanh, // Biến lưu lỗi nếu có khi tải tỉnh thành
    } = useDiaChi();

    // Sử dụng hook tùy chỉnh quản lý giỏ hàng
    const { gioHang, danhSachSanPhamTrongGio } = useGioHang();

    // Sử dụng hook tùy chỉnh quản lý giảm giá (đổi tên cho dễ hiểu)
    const {
        maGiamGiaNhapVao, // Mã giảm giá người dùng nhập
        maGiamGiaHopLe, // Thông tin mã giảm giá hợp lệ (nếu có)
        phanTramGiamGia, // Phần trăm giảm giá
        thongBaoGiamGia, // Thông báo về mã giảm giá
        xuLyThayDoiMaGiamGia, // Hàm xử lý khi người dùng nhập mã giảm giá
        xuLyApDungMaGiamGia, // Hàm xử lý khi người dùng bấm nút áp dụng mã giảm giá
    } = useMaGiamGia();

    // Hàm useEffect chạy sau khi component được render hoặc state thay đổi
    useEffect(() => {
        // Lấy token từ localStorage (nếu có)
        const token = localStorage.getItem('token');
        if (token) {
            try {
                // Giải mã token để lấy thông tin người dùng
                const thongTinNguoiDung = decodeToken(token);
                // Lấy mã người dùng từ thông tin đã giải mã
                setMaKhachHang(thongTinNguoiDung?.ma_nguoi_dung || null);
            } catch (error) {
                console.error('Lỗi khi giải mã token:', error);
                setMaKhachHang(null);
            }
        } else {
            setMaKhachHang(null);
        }
    }, []); // [] có nghĩa là useEffect này chỉ chạy một lần sau lần render đầu tiên

    // Hàm xử lý khi phương thức thanh toán thay đổi
    const handlePaymentMethodChange = (event) => {
        setPhuongThucThanhToan(event.target.value);
    };

    // Hàm lấy dữ liệu đơn hàng để gửi lên server
    const layDuLieuDonHang = () => {
        // Tìm thông tin chi tiết của tỉnh, huyện, xã đã chọn
        const tinhThanhDaChonData = danhSachTinhThanh.find(tinh => tinh.code === parseInt(maTinhThanhDaChon, 10));
        const quanHuyenDaChonData = danhSachQuanHuyen.find(huyen => huyen.code === parseInt(maQuanHuyenDaChon, 10));
        const phuongXaDaChonData = danhSachPhuongXa.find(xa => xa.code === parseInt(maPhuongXaDaChon, 10));

        // Tạo địa chỉ giao hàng đầy đủ
        const diaChiGiaoHang = `${diaChiCuThe}, ${phuongXaDaChonData?.name || ''}, ${quanHuyenDaChonData?.name || ''}, ${tinhThanhDaChonData?.name || ''}`;

        // Tạo ID đơn hàng duy nhất
        const maDonHang = taoID();

        // Định dạng ngày đặt hàng cho database
        const ngayDatHangFormatted = formatDateForDatabase(new Date());

        // Tạo đối tượng đơn hàng
        const donHang = {
            id: maDonHang, // ID đơn hàng
            makhachhangdathang: maKhachHang, // Mã khách hàng đặt hàng
            manhanvienxulydonhang: null, // Mã nhân viên xử lý (ban đầu là null)
            ngaydathang: ngayDatHangFormatted, // Ngày đặt hàng
            diachigiaohang: diaChiGiaoHang, // Địa chỉ giao hàng
            trangthai: 'Chờ xử lý', // Trạng thái đơn hàng ban đầu
            phuongthucthanhtoan: phuongThucThanhToan, // Phương thức thanh toán
            magiamgia: maGiamGiaHopLe?.id || null, // Mã giảm giá đã áp dụng (nếu có)
            tongtien: tinhTongTienSauGiamGia(tinhTongTienHang(gioHang), phanTramGiamGia) // Tổng tiền sau giảm giá
        };

        // Tạo mảng chi tiết đơn hàng từ danh sách sản phẩm trong giỏ
        const chiTietDonHang = danhSachSanPhamTrongGio.map(sanPham => ({
            id: taoID(), // ID chi tiết đơn hàng
            madonhang: maDonHang, // Mã đơn hàng cha
            matruyen: sanPham.ID, // Mã truyện
            soluong: sanPham.SoLuongTruyen // Số lượng mua
        }));

        // Trả về thông tin đơn hàng và chi tiết đơn hàng
        return { donHang, chiTietDonHang };
    };

    // Hàm xử lý khi người dùng bấm nút đặt hàng
    const handlePlaceOrder = async (event) => {
        event.preventDefault(); // Ngăn chặn hành động mặc định của form (reload trang)
        const { donHang, chiTietDonHang } = layDuLieuDonHang();

        try {
            // Gọi API để tạo đơn hàng mới
            const responseDonHang = await axios.post('http://localhost:3001/donhangs/', donHang);
            console.log('Đơn hàng đã được tạo:', donHang);

            // Lặp qua từng chi tiết đơn hàng để tạo các mục chi tiết đơn hàng
            for (const chiTiet of chiTietDonHang) {
                await axios.post('http://localhost:3001/chitietdonhangs/', chiTiet);
                console.log('Chi tiết đơn hàng đã được tạo:', chiTiet);

                try {
                    // Lấy thông tin truyện để cập nhật số lượng tồn kho
                    const responseTruyen = await axios.get(`http://localhost:3001/truyens/${chiTiet.matruyen}`);
                    // Giả sử response.data là một mảng chứa thông tin truyện
                    const thongTinTruyenHienTai = responseTruyen.data[0];
                    const soLuongConLai = thongTinTruyenHienTai.soluong - chiTiet.soluong;

                    // Kiểm tra nếu số lượng còn lại không âm
                    if (soLuongConLai >= 0) {
                        // Cập nhật số lượng tồn kho của truyện
                        await axios.put(`http://localhost:3001/truyens/${chiTiet.matruyen}`, { soluong: soLuongConLai });
                        console.log(`Đã cập nhật số lượng truyện ${chiTiet.matruyen} còn lại: ${soLuongConLai}`);
                    } else {
                        console.error(`Không đủ số lượng cho truyện ${chiTiet.matruyen}. Đã đặt: ${chiTiet.soluong}, Hiện có: ${thongTinTruyenHienTai.soluong}`);
                        alert(`Rất tiếc, số lượng truyện ${thongTinTruyenHienTai.tentruyen} không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return; // Dừng quá trình đặt hàng nếu không đủ số lượng
                    }
                } catch (error) {
                    console.error(`Lỗi khi lấy hoặc cập nhật số lượng truyện ${chiTiet.matruyen}:`, error);
                    alert(`Đã có lỗi xảy ra khi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return; // Dừng quá trình đặt hàng nếu có lỗi
                }
            }

            // Xóa giỏ hàng khỏi localStorage sau khi đặt hàng thành công
            localStorage.removeItem('GioHang');
            alert('Đặt hàng thành công!');
            // Chuyển hướng người dùng về trang chủ
            window.location.href = '/';
        } catch (error) {
            console.error('Lỗi khi tạo đơn hàng:', error);
            alert('Đã có lỗi xảy ra khi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                {/* Phần hiển thị các sản phẩm đã mua */}
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {danhSachSanPhamTrongGio.length > 0 ? (
                            danhSachSanPhamTrongGio.map(sanPham => (
                                <div className={styles['product-item']} key={sanPham.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={sanPham.AnhTruyen} alt={sanPham.TenTruyen} />
                                        <span className={styles['soluongsp']}>{sanPham.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{sanPham.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{sanPham.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    {/* Phần nhập và áp dụng mã giảm giá */}
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaNhapVao}
                            onChange={xuLyThayDoiMaGiamGia}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={xuLyApDungMaGiamGia}>
                            Áp dụng
                        </button>
                    </div>
                    {/* Hiển thị thông báo về mã giảm giá */}
                    {thongBaoGiamGia && (
                        <div className={`${maGiamGiaHopLe ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    {/* Phần hiển thị tổng tiền */}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{tinhTongTienHang(gioHang).toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{((tinhTongTienHang(gioHang) * phanTramGiamGia) / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{tinhTongTienSauGiamGia(tinhTongTienHang(gioHang), phanTramGiamGia).toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                {/* Form nhập địa chỉ giao hàng và chọn phương thức thanh toán */}
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handlePlaceOrder}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    {/* Dropdown chọn tỉnh thành */}
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={xuLyThayDoiTinhThanh}
                        value={maTinhThanhDaChon}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {danhSachTinhThanh.map(tinh => (
                            <option key={tinh.code} value={tinh.code}>{tinh.name}</option>
                        ))}
                    </select>
                    {/* Dropdown chọn quận huyện */}
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={xuLyThayDoiQuanHuyen}
                        value={maQuanHuyenDaChon}
                        disabled={!maTinhThanhDaChon} // Vô hiệu hóa nếu chưa chọn tỉnh
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {danhSachQuanHuyen.map(huyen => (
                            <option key={huyen.code} value={huyen.code}>{huyen.name}</option>
                        ))}
                    </select>
                    {/* Dropdown chọn phường xã */}
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={xuLyThayDoiPhuongXa}
                        value={maPhuongXaDaChon}
                        disabled={!maQuanHuyenDaChon} // Vô hiệu hóa nếu chưa chọn huyện
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {danhSachPhuongXa.map(xa => (
                            <option key={xa.code} value={xa.code}>{xa.name}</option>
                        ))}
                    </select>
                    {/* Input nhập địa chỉ cụ thể */}
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={diaChiCuThe}
                        onChange={(e) => setDiaChiCuThe(e.target.value)}
                    />
                    {/* Phần chọn phương thức thanh toán */}
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={phuongThucThanhToan === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={phuongThucThanhToan === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {/* Hiển thị thông tin chuyển khoản nếu chọn phương thức này */}
                    {phuongThucThanhToan === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    {/* Nút đặt hàng */}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
