import React, { useEffect } from "react";
import '../../component/grid.css';
import homeStyles from '../home/home.module.css';
import '../../component/style.css';
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import TruyenTheoQuangCaoData from "../TruyenTheoQuangCao"; // Đổi tên import
import DanhSachTruyen from "../renderTruyen";

const Home = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    // Mã quảng cáo bạn muốn hiển thị trên trang chủ
    const maQuangCaoHome = 'QC01'; // Thay 'QC01' bằng mã quảng cáo thực tế của bạn

    // Lấy dữ liệu truyện theo quảng cáo
    const { truyens: truyệnTheoQC, tenquangcao, loading, error } = TruyenTheoQuangCaoData({ maquangcao: maQuangCaoHome });

    return (
        <>
            {loading ? (
                <p>Đang tải truyện theo quảng cáo...</p>
            ) : error ? (
                <p>Lỗi tải dữ liệu: {error.message}</p>
            ) : (
                <DanhSachTruyen
                    truyens={truyệnTheoQC}
                    tieude={tenquangcao}
                    on_xemthem={true}
                    link_xemthem={`/quangcao/${maQuangCaoHome}`}
                    on_phantrang={false}
                    soLuongHienThi={8}
                />
            )}
        </>
    );
}

export default Home;
