import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';

const Payment = () => {
    // ... (các state và useEffect khác giữ nguyên)

    const handleApDungGiamGia = async (e) => {
        e.preventDefault(); // Ngăn form submit
        const maNhap = maGiamGiaInput.trim().toUpperCase();

        if (maNhap && !isApplyingCoupon) {
            setIsApplyingCoupon(true);
            try {
                const response = await axios.get('http://localhost:3001/giamgias/');
                const danhSachGiamGia = response.data;
                const maKhuyenMai = danhSachGiamGia.find(gg => gg.id.toUpperCase() === maNhap);

                if (maKhuyenMai) {
                    const ngayHienTai = new Date();
                    const ngayBatDau = new Date(maKhuyenMai.ngaybatdau);
                    const ngayKetThuc = new Date(maKhuyenMai.ngayketthuc);

                    if (ngayHienTai >= ngayBatDau && ngayHienTai <= ngayKetThuc) {
                        setMaGiamGia(maKhuyenMai);
                        setPhanTramGiamGia(maKhuyenMai.phantramgiamgia);
                        setThongBaoGiamGia(`Áp dụng mã ${maNhap} thành công! Giảm ${maKhuyenMai.phantramgiamgia}%`);
                    } else if (ngayHienTai < ngayBatDau) {
                        setPhanTramGiamGia(0);
                        setThongBaoGiamGia(`Mã ${maNhap} chưa đến thời gian áp dụng!`);
                    } else {
                        setPhanTramGiamGia(0);
                        setThongBaoGiamGia(`Mã ${maNhap} đã hết hạn!`);
                    }
                } else {
                    setPhanTramGiamGia(0); // Đặt lại phần trăm giảm giá về 0 khi mã không tồn tại
                    setThongBaoGiamGia(`Mã ${maNhap} không tồn tại!`);
                }
            } catch (error) {
                console.error('Lỗi gọi API giảm giá:', error);
                setPhanTramGiamGia(0); // Đặt lại phần trăm giảm giá về 0 khi có lỗi API
                setThongBaoGiamGia('Lỗi kiểm tra mã giảm giá.');
            } finally {
                setIsApplyingCoupon(false);
            }
        } else if (!maNhap) {
            setPhanTramGiamGia(0); // Đặt lại phần trăm giảm giá về 0 khi không có mã nhập
            setThongBaoGiamGia('Vui lòng nhập mã giảm giá.');
        }
    };

    // Tính tổng tiền sau khi giảm giá
    const calculateTotalPrice = () => {
        const tongTienHang = Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
        const tienGiamGia = (tongTienHang * phanTramGiamGia) / 100;
        return tongTienHang - tienGiamGia;
    };

    // Tính tổng tiền trước khi giảm giá
    const calculateTamTinh = () => {
        return Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                {/* Phần sản phẩm đã mua */}
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {gioHangArray.length > 0 ? (
                            gioHangArray.map(item => (
                                <div className={styles['product-item']} key={item.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={item.AnhTruyen} alt={item.TenTruyen} />
                                        <span className={styles['soluongsp']}>{item.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{item.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{item.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    {/* Form áp dụng mã giảm giá */}
                    <form id="giamgia" className={styles['giamgia']} onSubmit={handleApDungGiamGia}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaInput}
                            onChange={handleMaGiamGiaInputChange}
                        />
                        <button type="submit" className={styles['giamgia-button']} disabled={isApplyingCoupon}>
                            {isApplyingCoupon ? 'Đang kiểm tra...' : 'Áp dụng'}
                        </button>
                    </form>
                    {/* Hiển thị thông báo mã giảm giá */}
                    {thongBaoGiamGia && (
                        <div className={`${phanTramGiamGia > 0 ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    {/* Hiển thị tổng tiền */}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{calculateTamTinh().toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{((calculateTamTinh() * phanTramGiamGia) / 100)?.toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{calculateTotalPrice().toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                {/* Phần địa chỉ giao hàng và thanh toán */}
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    {/* Chọn tỉnh/thành phố */}
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleCityChange}
                        value={selectedCity}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {cities.map(city => (
                            <option key={city.code} value={city.code}>{city.name}</option>
                        ))}
                    </select>
                    {/* Chọn quận/huyện */}
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleDistrictChange}
                        value={selectedDistrict}
                        disabled={!selectedCity}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {districts.map(district => (
                            <option key={district.code} value={district.code}>{district.name}</option>
                        ))}
                    </select>
                    {/* Chọn phường/xã */}
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleWardChange}
                        value={selectedWard}
                        disabled={!selectedDistrict}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {wards.map(ward => (
                            <option key={ward.code} value={ward.code}>{ward.name}</option>
                        ))}
                    </select>
                    {/* Nhập địa chỉ cụ thể */}
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={specificAddress}
                        onChange={handleSpecificAddressChange}
                    />
                    {/* Chọn phương thức thanh toán */}
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {/* Hiển thị QR code nếu chọn chuyển khoản */}
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    {/* Nút đặt hàng */}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
