import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Api_product = () => {
    const { id_truyen } = useParams(); // Lấy id_truyen từ URL
    const [truyen, setTruyen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Gọi API để lấy dữ liệu truyện
        axios.get(`http://localhost:3001/truyens/${id_truyen}`)
            .then(response => {
                console.log('Dữ liệu API:', response.data); // Log dữ liệu để kiểm tra
                setTruyen(response.data); // Lưu dữ liệu truyện vào state
                setLoading(false); // Đặt trạng thái loading là false
            })
            .catch(() => {
                setError('Có lỗi xảy ra'); // Xử lý lỗi khi gọi API
                setLoading(false); // Đặt trạng thái loading là false
            });
    }, [id_truyen]); // Gọi lại khi id_truyen thay đổi

    // Kiểm tra các trạng thái và trả về giao diện tương ứng
    if (loading) return <div>Đang tải...</div>;
    if (error) return <div>{error}</div>;
    if (!truyen) return <div>Không tìm thấy truyện</div>;

    return (
        <div>
            {loading && <div>Đang tải...</div>} {/* Hiển thị khi đang tải dữ liệu */}
            {error && <div>{error}</div>} {/* Hiển thị khi có lỗi */}
            {!loading && !error && truyen ? (
                <div>
                    <h1>{truyen.tentruyen ? truyen.tentruyen : 'Chưa có tên truyện'}</h1>
                    <p><strong>Tác giả:</strong> {truyen.tacgia ? truyen.tacgia : 'Chưa có tác giả'}</p>
                    <p><strong>Giá bán:</strong> {truyen.giaban ? `${truyen.giaban} VNĐ` : 'Chưa có giá bán'}</p>
                    <p><strong>Giá gốc:</strong> {truyen.giagoc ? `${truyen.giagoc} VNĐ` : 'Chưa có giá gốc'}</p>
                    <p><strong>ISBN:</strong> {truyen.isbn ? truyen.isbn : 'Chưa có ISBN'}</p>
                    <p><strong>Định dạng:</strong> {truyen.dinhdang ? truyen.dinhdang : 'Chưa có định dạng'}</p>
                    <p><strong>Đối tượng:</strong> {truyen.doituong ? truyen.doituong : 'Chưa có đối tượng'}</p>
                    <p><strong>Số lượng:</strong> {truyen.soluong ? truyen.soluong : 'Chưa có số lượng'}</p>
                    <p><strong>Số trang:</strong> {truyen.sotrang ? truyen.sotrang : 'Chưa có số trang'}</p>
                    <p><strong>Trọng lượng:</strong> {truyen.trongluong ? truyen.trongluong : 'Chưa có trọng lượng'}</p>
                    {/* Kiểm tra ảnh */}
                    <p><strong>Ảnh:</strong>
                        {truyen.anhtruyen ?
                            <img src={`path/to/images/${truyen.anhtruyen}`} alt={truyen.tentruyen} /> :
                            'Chưa có ảnh'}
                    </p>
                </div>
            ) : (
                <div>Không có thông tin truyện</div>
            )}
        </div>
    );

};

export default Api_product;
