import React, { useEffect, useState, useRef, useCallback } from "react";
import styles from './header.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import logo from '../img/logo.png';
import axios from 'axios';
import { Link } from 'react-router-dom';
import updateCartCount from '../updateCartCount';

const Header = () => {
    const [theLoaisMobile, setTheLoaisMobile] = useState([]);
    const [loadingMobile, setLoadingMobile] = useState(true);
    const cartCountRefPC = useRef(null);
    const cartCountRefMobile = useRef(null);
    const [tenKhach, setTenKhach] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [renderTrigger, setRenderTrigger] = useState(false); // State để kích hoạt re-render

    const fetchTheLoaisMobile = useCallback(async () => {
        setLoadingMobile(true);
        try {
            const response = await axios.get('http://localhost:3001/theloais');
            setTheLoaisMobile(response.data);
        } catch (error) {
            console.error("Lỗi khi tải danh mục thể loại (mobile):", error);
        } finally {
            setLoadingMobile(false);
        }
    }, []);

    useEffect(() => {
        fetchTheLoaisMobile();
        updateCartCount(cartCountRefPC, cartCountRefMobile);

        const storedTenKhachHang = localStorage.getItem('tenKhachHang');
        if (storedTenKhachHang) {
            setTenKhach(storedTenKhachHang);
            setIsLoggedIn(true);
        } else {
            setIsLoggedIn(false);
        }

        const handleStorageChange = () => {
            const currentTenKhachHang = localStorage.getItem('tenKhachHang');
            if (currentTenKhachHang !== tenKhach) {
                setTenKhach(currentTenKhachHang || '');
                setIsLoggedIn(!!currentTenKhachHang);
                setRenderTrigger(prev => !prev); // Toggle state để kích hoạt re-render
            }
        };

        window.addEventListener('storage', handleStorageChange);

        return () => {
            window.removeEventListener('storage', handleStorageChange);
        };
    }, [fetchTheLoaisMobile, cartCountRefPC, cartCountRefMobile, tenKhach]); // Thêm tenKhach vào dependency array

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('tenKhachHang');
        setIsLoggedIn(false);
        setTenKhach('');
        setRenderTrigger(prev => !prev); // Kích hoạt re-render sau khi đăng xuất
    };

    return (
        <header id="header" className={styles.header}>
            {/* ... Phần header top ... */}
            <div className={styles['header-bottom']}>
                <div className="pc-tablet-header hide-on-mobile">
                    <div className="grid wide">
                        <div className="row flex-center">
                            {/* ... Form tìm kiếm và logo ... */}
                            <div className="col l-4 m-4 c-4 displayflex header-bottom-right">
                                <div className="user-connect flex-center">
                                    {!isLoggedIn ? (
                                        <div id="LoginFalse" className="flex-center">
                                            <Link to="/dangnhap" className={`a-black dangnhap hover-red flex-center hide-on-tablet ${styles.dangnhap}`} style={{ textDecoration: 'none' }}>
                                                {/* ... Link Đăng nhập ... */}
                                            </Link>
                                            <div className={`dangky hover-red flex-center hide-on-tablet ${styles.dangky}`}>
                                                {/* ... Link Đăng ký ... */}
                                            </div>
                                        </div>
                                    ) : (
                                        <div id="LoginTrue" className="flex-center">
                                            <div className={`user hover-red flex-center ${styles.user}`} style={{ display: "flex" }}>
                                                <i className="fas fa-user" style={{ marginRight: 5 }} />
                                                <span id="NameUserLogin">{tenKhach || 'Khách'}</span>
                                            </div>
                                            <div className={`dangxuat hover-red flex-center hide-on-tablet hide-on-pclow ${styles.dangxuat}`} onClick={handleLogout} style={{ cursor: 'pointer' }}>
                                                {/* ... Link Đăng xuất ... */}
                                            </div>
                                        </div>
                                    )}
                                </div>
                                {/* ... Giỏ hàng và yêu thích ... */}
                            </div>
                        </div>
                    </div>
                </div>
                {/* ... Phần header mobile ... */}
            </div>
        </header>
    );
};

export default Header;
