function updateCartCount(cartCountRefPC, cartCountRefMobile) {
    // Lấy dữ liệu giỏ hàng từ Local Storage.
    // Nếu không có dữ liệu, gán một object rỗng.
    const duLieuGioHang = localStorage.getItem('GioHang');
    let gioHang = {};
    if (duLieuGioHang !== null) {
        gioHang = JSON.parse(duLieuGioHang);
    }

    // Lấy tất cả các giá trị (là thông tin sản phẩm) từ object giỏ hàng vào một mảng.
    const cacSanPhamTrongGioHang = Object.values(gioHang);

    // Khởi tạo biến để tính tổng số lượng sản phẩm.
    let tongSoLuong = 0;

    // Sử dụng vòng lặp for để duyệt qua từng sản phẩm trong mảng cacSanPhamTrongGioHang.
    for (let i = 0; i < cacSanPhamTrongGioHang.length; i++) {
        const sanPham = cacSanPhamTrongGioHang[i];
        let soLuongSanPham = sanPham.SoLuongTruyen;
        tongSoLuong = tongSoLuong + soLuongSanPham;
    }

    let giaTriHienThi;
    if (tongSoLuong < 10) {
        giaTriHienThi = tongSoLuong;
    } else {
        giaTriHienThi = "9+";
    }

    if (cartCountRefPC !== null && cartCountRefPC.current !== null) {
        cartCountRefPC.current.textContent = giaTriHienThi;
    }

    if (cartCountRefMobile !== null && cartCountRefMobile.current !== null) {
        cartCountRefMobile.current.textContent = giaTriHienThi;
    }
}

export default updateCartCount;
