// Ví dụ trong utils.js hoặc cartUtils.js
import styles from '../components/header/header.module.css';

function updateCartCount() {
    let count = getTotalQuantity();
    // Sử dụng thuộc tính của đối tượng styles để tạo selector
    let SLGioHang = document.querySelectorAll(`.${styles.giohang} .${styles.soluong}`);
    let SLGioHangMobile = document.querySelectorAll(`.mobile-header .${styles.giohang} .${styles.soluong}`); // Thêm selector cho mobile nếu cần

    SLGioHang.forEach(phanTuSoLuong => {
        phanTuSoLuong.textContent = count < 10 ? count : "9+";
    });

    SLGioHangMobile.forEach(phanTuSoLuong => {
        phanTuSoLuong.textContent = count < 10 ? count : "9+";
    });
}

// Hàm tính tổng số lượng sản phẩm trong giỏ hàng từ localStorage
function getTotalQuantity() {
    let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
    return Object.values(GioHang).reduce((acc, item) => acc + item.SoLuongTruyen, 0);
}

export default updateCartCount;
