import React, { useEffect, useState } from "react";
import '../../component/grid.css';
import banner2 from '../banner2/banner2.module.css';
import '../../component/style.css';
import { useParams, Link } from 'react-router-dom'; // Import useParams để lấy tham số từ URL và Link để tạo liên kết
import axios from 'axios';

const Banner2 = () => {
    const { categoryType, categoryId } = useParams(); // Lấy categoryType ('quangcao' hoặc 'theloai') và categoryId từ URL
    const [categoryName, setCategoryName] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCategoryName = async () => {
            setLoading(true);
            try {
                let apiUrl = '';
                if (categoryType === 'quangcao') {
                    apiUrl = `http://localhost:3001/quangcaos/${categoryId}`;
                } else if (categoryType === 'theloai') {
                    apiUrl = `http://localhost:3001/theloais/${categoryId}`;
                }

                if (apiUrl) {
                    const response = await axios.get(apiUrl);
                    if (response.data) {
                        setCategoryName(response.data.tenquangcao || response.data.tentheloai || '');
                    }
                }
            } catch (error) {
                console.error("Lỗi khi tải tên danh mục:", error);
                setCategoryName('Không tìm thấy');
            } finally {
                setLoading(false);
            }
        };

        fetchCategoryName();
    }, [categoryType, categoryId]);

    if (loading) {
        return (
            <div className={banner2.banner}>
                <div className={`grid wide ${banner2.title}`}>
                    <div className="row">
                        <div className={`col l-12 m-12 c-12 title-text`}>
                            <h2 className="title-top" style={{ marginBottom: 3 }}>
                                Đang tải...
                            </h2>
                            <div className="title-link">
                                <Link className="a-white hover-red" to="/">
                                    Trang chủ
                                </Link>
                                <span>/ Đang tải...</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className={banner2.banner}>
            <div className={`grid wide ${banner2.title}`}>
                <div className="row">
                    <div className={`col l-12 m-12 c-12 title-text`}>
                        <h2 className="title-top" style={{ marginBottom: 3 }}>
                            {categoryName}
                        </h2>
                        <div className="title-link">
                            <Link className="a-white hover-red" to="/">
                                Trang chủ
                            </Link>
                            <span>/ {categoryName}</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Banner2;
