// utils.js

export const tinhTongTienHang = (cartObject) => {
    return Object.values(cartObject).reduce(
        (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen, // Giữ nguyên
        0
    );
};

export const tinhTongTienSauGiamGia = (totalAmount, discountPercentage) => {
    const discountAmount = (totalAmount * discountPercentage) / 100;
    return totalAmount - discountAmount;
};

export const taoID = () => {
    return Date.now().toString(36).slice(-5) + Math.random().toString(36).slice(-3);
};

export const formatDateForDatabase = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
};
