import React, { useEffect, useState } from "react";
import "../../component/grid.css";
import banner2 from "../banner2/banner2.module.css";
import "../../component/style.css";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

const Banner2 = () => {
    const { id_theloai, id_quangcao } = useParams();
    const [tenDanhMuc, setTenDanhMuc] = useState("");
    const [dangTai, setDangTai] = useState(true);

    useEffect(() => {
        const layTenDanhMuc = async () => {
            setDangTai(true);
            let maDanhMuc;
            let urlApi = "";

            // Trường hợp nếu là trang thể loại
            if (id_theloai) {
                maDanhMuc = id_theloai;
                urlApi = `http://localhost:3001/theloais/${maDanhMuc}`;
                console.log("Trang thể loại, maDanhMuc:", maDanhMuc, "urlApi:", urlApi);
            }
            // Trường hợp nếu là trang quảng cáo
            else if (id_quangcao) {
                maDanhMuc = id_quangcao;
                urlApi = `http://localhost:3001/quangcaos/${maDanhMuc}`;
                console.log("Trang quảng cáo, maDanhMuc:", maDanhMuc, "urlApi:", urlApi);
            }

            if (urlApi && maDanhMuc) {
                try {
                    console.log("Bắt đầu gọi API:", urlApi);
                    const response = await axios.get(urlApi);
                    console.log("Kết quả API:", response);
                    if (response.data) {
                        console.log("Dữ liệu API:", response.data);
                        // Lấy tên dựa trên loại trang
                        if (id_theloai) {
                            setTenDanhMuc(response.data[0]?.tentheloai || "");
                        } else if (id_quangcao) {
                            setTenDanhMuc(response.data[0]?.tenquangcao || "");
                        }
                        console.log("Tên danh mục đã đặt:", tenDanhMuc);
                    }
                } catch (error) {
                    console.error("Lỗi khi tải tên danh mục:", error);
                    setTenDanhMuc("Không tìm thấy");
                } finally {
                    setDangTai(false);
                    console.log("Đã hoàn thành tải");
                }
            } else {
                setDangTai(false);
                setTenDanhMuc("");
            }
        };

        layTenDanhMuc();
        // useEffect sẽ chạy lại khi id_theloai hoặc id_quangcao thay đổi
    }, [id_theloai, id_quangcao]);

    if (dangTai) {
        return (
            <div className={banner2.banner}>
                <div className={`grid wide ${banner2.title}`}>
                    <div className="row">
                        <div className={`col l-12 m-12 c-12 title-text`}>
                            <h2 className="title-top" style={{ marginBottom: 3 }}>
                                Đang tải...
                            </h2>
                            <div className="title-link">
                                <Link className="a-white hover-red" to="/">
                                    Trang chủ
                                </Link>
                                <span>/ Đang tải...</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className={banner2.banner}>
            <div className={`grid wide ${banner2.title}`}>
                <div className="row">
                    <div className={`col l-12 m-12 c-12 title-text`}>
                        <h2 className="title-top" style={{ marginBottom: 3, textTransform: 'uppercase' }}>
                            {tenDanhMuc}
                        </h2>
                        <div className="title-link">
                            <Link className="a-white hover-red" to="/">
                                Trang chủ
                            </Link>
                            <span>/ {tenDanhMuc}</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Banner2;
