import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import '../../component/grid.css';
import stylesDanhMuc from '../category/category.module.css'; // Import CSS module cho trang danh mục
import '../../component/style.css';
import '../home/home.module.css';
import updateCartCount from '../updateCartCount';

const SO_TRUYEN_MOI_TRANG = 12; // Số lượng truyện hiển thị trên mỗi trang

const TrangDanhMuc = () => {
    useEffect(() => {
        updateCartCount();
    }, []);

    const { id_theloai } = useParams();
    const [trangHienTai, setTrangHienTai] = useState(1); // State lưu trữ số trang hiện tại, mặc định là trang 1
    const [tongSoTruyen, setTongSoTruyen] = useState(0); // State lưu trữ tổng số truyện thuộc thể loại
    const [truyenTrenTrang, setTruyenTrenTrang] = useState([]); // State lưu trữ mảng truyện sẽ được hiển thị trên trang hiện tại

    useEffect(() => {
        const layTruyenTheoTheLoai = async () => {
            try {
                // Gọi API để lấy danh sách truyện theo mã thể loại
                const response = await fetch(`http://localhost:3001/truyens?matheloai=${id_theloai}`);
                const data = await response.json();
                setTongSoTruyen(data.length); // Cập nhật tổng số truyện

                // Tính toán index bắt đầu và kết thúc cho trang hiện tại
                const indexBatDau = (trangHienTai - 1) * SO_TRUYEN_MOI_TRANG;
                const indexKetThuc = indexBatDau + SO_TRUYEN_MOI_TRANG;
                const truyenChoTrang = data.slice(indexBatDau, indexKetThuc); // Lấy ra số truyện cần hiển thị cho trang hiện tại
                setTruyenTrenTrang(truyenChoTrang); // Cập nhật state truyện trên trang
            } catch (error) {
                console.error("Lỗi khi tải truyện theo thể loại:", error);
                setTongSoTruyen(0);
                setTruyenTrenTrang([]);
            }
        };

        layTruyenTheoTheLoai();
    }, [id_theloai, trangHienTai]); // Gọi lại effect khi mã thể loại hoặc trang hiện tại thay đổi

    const tongSoTrang = Math.ceil(tongSoTruyen / SO_TRUYEN_MOI_TRANG); // Tính tổng số trang

    const chuyenTrang = (soTrang) => {
        setTrangHienTai(soTrang); // Cập nhật trang hiện tại khi người dùng chọn trang khác
    };

    const hienThiSoTrang = () => {
        const cacSoTrang = [];
        for (let i = 1; i <= tongSoTrang; i++) {
            cacSoTrang.push(
                <div
                    key={i}
                    className={`${stylesDanhMuc.trang} ${trangHienTai === i ? stylesDanhMuc.trangchon : ''}`}
                    onClick={() => chuyenTrang(i)}
                >
                    <span>
                        <b>{i}</b>
                    </span>
                </div>
            );
        }

        // Logic để hiển thị các nút trang một cách thông minh (ví dụ: 1 2 3 ... 8 9)
        if (tongSoTrang <= 7) {
            return cacSoTrang;
        }

        const trangDau = cacSoTrang[0];
        const trangCuoi = cacSoTrang[tongSoTrang - 1];
        const indexTrangHienTai = trangHienTai - 1;

        if (indexTrangHienTai < 3) {
            return [trangDau, cacSoTrang[1], cacSoTrang[2], <span key="dots-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, trangCuoi];
        }

        if (indexTrangHienTai > tongSoTrang - 4) {
            return [trangDau, <span key="dots-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, cacSoTrang[tongSoTrang - 3], cacSoTrang[tongSoTrang - 2], trangCuoi];
        }

        return [trangDau, <span key="dots-middle-start" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, cacSoTrang[indexTrangHienTai - 1], cacSoTrang[indexTrangHienTai], cacSoTrang[indexTrangHienTai + 1], <span key="dots-middle-end" className="flex-center" style={{ margin: "0px 10px" }}>...</span>, trangCuoi];
    };

    return (
        <div className="grid wide list-product" style={{ marginTop: 25 }}>
            <div className="row">
                {/* Duyệt qua mảng truyện đã được phân trang để hiển thị */}
                {truyenTrenTrang.map(truyen => (
                    <div key={truyen.id} className="col l-2-4 m-3 c-4">
                        <div className="product">
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className="product-img">
                                    <img src={truyen.anhtruyen} alt={truyen.tentruyen} className="product-img" />
                                </div>
                                <h4 className="product-name">{truyen.tentruyen}</h4>
                                <div className="product-price">
                                    <span className="current-price"><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc && (
                                        <span className="original-price">
                                            <s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s>
                                        </span>
                                    )}
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>

            {/* Hiển thị các nút phân trang nếu có nhiều hơn 1 trang */}
            {tongSoTrang > 1 && (
                <div className={stylesDanhMuc.phantrang}>
                    {hienThiSoTrang()}
                </div>
            )}
        </div>
    );
}

export default TrangDanhMuc;
