// export default ArrowInputTypeNumber;
import React from 'react';

function ArrowInputTypeNumber({ value, onChange }) {
    const handleDecrease = () => {
        const newValue = Math.max(1, value - 1); // Không cho nhỏ hơn 1
        onChange(newValue);
    };

    const handleIncrease = () => {
        const newValue = value + 1;
        onChange(newValue);
    };

    const handleInputChange = (e) => {
        const val = parseInt(e.target.value.replace(/\D/g, ''), 10);
        if (!isNaN(val)) {
            onChange(val);
        }
    };

    return (
        <div className="ArrowInputTypeNumber">
            <button className="button-down flex-center" onClick={decrease}>
                <i className="fas fa-chevron-down" />
            </button>
            <input
                type="text"
                className="input-soluong"
                value={value}
                onChange={handleInputChange}
                onBlur={handleBlur}
                onKeyDown={(e) => e.key === 'Enter' && e.target.blur()}
            />
            <button className="button-up flex-center" onClick={increase}>
                <i className="fas fa-chevron-up" />
            </button>
        </div>
    );
}

export default ArrowInputTypeNumber;
