import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';
import { decodeToken } from '../decodeToken';
import { useDiaChi } from './useDiaChi'; // Custom hook quản lý địa chỉ
import { useGioHang } from './useGioHang'; // Custom hook quản lý giỏ hàng
import { useGiamGia as useMaGiamGia } from './useGiamGia'; // Custom hook quản lý giảm giá (đã sửa import)
import { tinhTongTienHang, tinhTongTienSauGiamGia, taoID, formatDateForDatabase } from './utils'; // Các hàm tiện ích

const Payment = () => {
    // State cho phương thức thanh toán
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    // State cho địa chỉ cụ thể
    const [specificAddress, setSpecificAddress] = useState('');
    // State cho mã khách hàng đặt hàng
    const [customerId, setCustomerId] = useState(null);

    // Sử dụng custom hook quản lý địa chỉ
    const {
        provinceList, // Danh sách tỉnh/thành phố
        districtList, // Danh sách quận/huyện
        wardList, // Danh sách phường/xã
        selectedProvince, // Tỉnh/thành phố đã chọn
        selectedDistrict, // Quận/huyện đã chọn
        selectedWard, // Phường/xã đã chọn
        handleProvinceChange, // Hàm xử lý khi chọn tỉnh/thành phố
        handleDistrictChange, // Hàm xử lý khi chọn quận/huyện
        handleWardChange, // Hàm xử lý khi chọn phường/xã
        provincesLoading, // Trạng thái loading dữ liệu tỉnh/thành phố (nếu có trong useDiaChi)
        provincesError, // Lỗi khi fetch dữ liệu tỉnh/thành phố (nếu có trong useDiaChi)
    } = useDiaChi();

    // Sử dụng custom hook quản lý giỏ hàng
    const { cartObject, cartItemList } = useGioHang(); // cartObject: Object giỏ hàng, cartItemList: Mảng sản phẩm trong giỏ

    // Sử dụng custom hook quản lý giảm giá (đã sửa tên biến)
    const {
        discountCodeInput, // Mã giảm giá người dùng nhập
        validDiscountCode, // Mã giảm giá hợp lệ (object)
        discountPercentage, // Phần trăm giảm giá
        discountMessage, // Thông báo về mã giảm giá
        handleDiscountCodeInputChange, // Hàm xử lý khi nhập mã giảm giá
        handleApplyDiscount, // Hàm xử lý khi áp dụng mã giảm giá
    } = useMaGiamGia();

    // Lấy mã khách hàng từ token khi component mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decoded = decodeToken(token);
                setCustomerId(decoded?.ma_nguoi_dung || null);
            } catch (error) {
                console.error('Lỗi khi giải mã token:', error);
                setCustomerId(null);
            }
        } else {
            setCustomerId(null);
        }
    }, []);

    // Hàm xử lý khi thay đổi phương thức thanh toán
    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    // Hàm lấy dữ liệu đơn hàng để tạo request
    const getOrderData = () => {
        const selectedProvinceData = provinceList.find(province => province.code === parseInt(selectedProvince, 10));
        const selectedDistrictData = districtList.find(district => district.code === parseInt(selectedDistrict, 10));
        const selectedWardData = wardList.find(ward => ward.code === parseInt(selectedWard, 10));

        const deliveryAddress = `${specificAddress}, ${selectedWardData?.name || ''}, ${selectedDistrictData?.name || ''}, ${selectedProvinceData?.name || ''}`;
        const orderId = taoID();
        const orderDateFormatted = formatDateForDatabase(new Date());

        const order = {
            id: orderId,
            customerId: customerId,
            staffId: null,
            orderDate: orderDateFormatted,
            deliveryAddress: deliveryAddress,
            orderStatus: 'Chờ xử lý',
            paymentMethod: paymentMethod,
            discountCode: validDiscountCode?.id || null,
            totalAmount: tinhTongTienSauGiamGia(tinhTongTienHang(cartObject), discountPercentage)
        };

        const orderDetails = cartItemList.map(item => ({
            id: taoID(),
            orderId: orderId,
            bookId: item.ID,
            quantity: item.SoLuongTruyen
        }));

        return { order, orderDetails };
    };

    // Hàm xử lý khi đặt hàng
    const handlePlaceOrder = async (event) => {
        event.preventDefault();
        const { order, orderDetails } = getOrderData();

        try {
            const orderResponse = await axios.post('http://localhost:3001/donhangs/', order);
            console.log('Đơn hàng đã được tạo:', order);

            for (const orderDetail of orderDetails) {
                await axios.post('http://localhost:3001/chitietdonhangs/', orderDetail);
                console.log('Chi tiết đơn hàng đã được tạo:', orderDetail);

                try {
                    const bookResponse = await axios.get(`http://localhost:3001/truyens/${orderDetail.bookId}`);
                    const currentBook = bookResponse.data[0];
                    const remainingQuantity = currentBook.soluong - orderDetail.quantity;

                    if (remainingQuantity >= 0) {
                        await axios.put(`http://localhost:3001/truyens/${orderDetail.bookId}`, { soluong: remainingQuantity });
                        console.log(`Đã cập nhật số lượng truyện ${orderDetail.bookId} còn lại: ${remainingQuantity}`);
                    } else {
                        console.error(`Không đủ số lượng cho truyện ${orderDetail.bookId}. Đã đặt: ${orderDetail.quantity}, Hiện có: ${currentBook.soluong}`);
                        alert(`Rất tiếc, số lượng truyện ${currentBook.tentruyen} không đủ. Vui lòng kiểm tra lại giỏ hàng.`);
                        return; // Dừng quá trình đặt hàng
                    }
                } catch (error) {
                    console.error(`Lỗi khi lấy hoặc cập nhật số lượng truyện ${orderDetail.bookId}:`, error);
                    alert(`Đã có lỗi xảy ra khi cập nhật số lượng truyện. Vui lòng thử lại sau.`);
                    return; // Dừng quá trình đặt hàng
                }
            }

            console.log(older);

            // Xóa giỏ hàng sau khi đặt hàng thành công
            localStorage.removeItem('GioHang');
            alert('Đặt hàng thành công!');
            window.location.href = '/'; // Chuyển hướng về trang chủ
        } catch (error) {
            console.error('Lỗi khi tạo đơn hàng:', error);
            alert('Đã có lỗi xảy ra khi đặt hàng. Vui lòng thử lại sau.');
        }
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {cartItemList.length > 0 ? (
                            cartItemList.map(item => (
                                <div className={styles['product-item']} key={item.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={item.AnhTruyen} alt={item.TenTruyen} />
                                        <span className={styles['soluongsp']}>{item.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{item.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{item.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={discountCodeInput}
                            onChange={handleDiscountCodeInputChange}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={handleApplyDiscount}>
                            Áp dụng
                        </button>
                    </div>
                    {discountMessage && (
                        <div className={`${validDiscountCode ? styles['success-apply'] : styles['fail-apply']}`}>
                            {discountMessage}
                        </div>
                    )}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{tinhTongTienHang(cartObject).toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {discountPercentage > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{(tinhTongTienHang(cartObject) * discountPercentage / 100).toLocaleString('vi-VN')}đ ({discountPercentage}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{tinhTongTienSauGiamGia(tinhTongTienHang(cartObject), discountPercentage).toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>

                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handlePlaceOrder}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleProvinceChange}
                        value={selectedProvince}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {provinceList.map(province => (
                            <option key={province.code} value={province.code}>{province.name}</option>
                        ))}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleDistrictChange}
                        value={selectedDistrict}
                        disabled={!selectedProvince}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {districtList.map(district => (
                            <option key={district.code} value={district.code}>{district.name}</option>
                        ))}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleWardChange}
                        value={selectedWard}
                        disabled={!selectedDistrict}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {wardList.map(ward => (
                            <option key={ward.code} value={ward.code}>{ward.name}</option>
                        ))}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={specificAddress}
                        onChange={(e) => setSpecificAddress(e.target.value)}
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
