import React, { useEffect } from "react";
import '../../component/grid.css';
import styles from './home.module.css'; // Import CSS Module for home styles
import responsiveStyles from './responsive.module.css'; // Import CSS Module for responsive styles
import '../../component/style.css';
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import TruyenTheoQuangCao from "../truyentheoquangcao";

const Home = () => {
    useEffect(() => {
        updateCartCount();

        function checkScreenSize() {
            const checkSizeMobile = document.querySelectorAll('.checkSizeMobile');
            checkSizeMobile.forEach(item => {
                if (window.innerWidth > 520) {
                    item.classList.remove('c-6');
                    item.classList.add('c-4');
                } else {
                    item.classList.remove('c-4');
                    item.classList.add('c-6');
                }
            });
        }
        // Gọi function ngay khi component render
        checkScreenSize();

        // Lắng nghe sự kiện resize
        window.addEventListener('resize', checkScreenSize);
        return () => {
            window.removeEventListener('resize', checkScreenSize);
        };
    }, []);

    return (
        <>
            <TruyenTheoQuangCao maquangcao="QC001" soluong={5} />
            <TruyenTheoQuangCao maquangcao="QC002" soluong={5} />
            <TruyenTheoQuangCao maquangcao="QC003" soluong={5} />
            <img className={styles['img-title']} src={banner_home_1} alt="Banner 1" />
            <TruyenTheoQuangCao maquangcao="lb7ssmtf" soluong={5} />
            <img className={styles['img-title']} src={banner_home_2} alt="Banner 2" />
            <TruyenTheoQuangCao maquangcao="lbmcb1jd" soluong={5} />
            <img className={styles['img-title']} src={banner_home_3} alt="Banner 3" />
            <TruyenTheoQuangCao maquangcao="lbwst2lr" soluong={5} />
            <img className={styles['img-title']} src={banner_home_4} alt="Banner 4" />
            <TruyenTheoQuangCao maquangcao="lc4ymoui" soluong={5} />
            <TruyenTheoQuangCao maquangcao="lcf1sre5" soluong={5} />
            <TruyenTheoQuangCao maquangcao="lcr3g6f2" soluong={5} />
        </>
    )
}

export default Home;
