import React, { useEffect } from "react";
import { useParams } from 'react-router-dom';
import '../../component/grid.css';
import category from '../category/category.module.css';
import '../../component/style.css';
import '../home/responsive.css';
import TruyenTheoQuangCao from "../truyentheoquangcao";
import updateCartCount from '../updateCartCount';

const Ads = () => {
    useEffect(() => {
        updateCartCount();
    });

    const { id_quangcao } = useParams();



    return (
        <div className="grid wide list-product" style={{ marginTop: 25 }}>
            <TruyenTheoQuangCao maquangcao={id_quangcao} soluong={999} on_xemthem={false} />

            <div className={category.phantrang}>
                <div className={`${category.trang} ${category.trangchon}`} href="#">
                    <span>
                        <b>1</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>2</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>3</b>
                    </span>
                </div>
                <span className="flex-center" style={{ margin: "0px 20px" }}>
                    ...
                </span>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>8</b>
                    </span>
                </div>
                <div className={`${category.trang}`} href="#">
                    <span>
                        <b>9</b>
                    </span>
                </div>
            </div>
        </div>
    );
}

export default Ads;
