import axios from 'axios';

var citis = document.getElementById("city");
var districtsSelect = document.getElementById("district");
var wardsSelect = document.getElementById("ward");
var Parameter = {
    url: "https://raw.githubusercontent.com/kenzouno1/DiaGioiHanhChinhVN/master/data.json",
    method: "GET",
    responseType: "application/json",
};
var promise = axios(Parameter);
promise.then(function (result) {
    renderCity(result.data);
});

function renderCity(data) {
    for (const x of data) {
        citis.options[citis.options.length] = new Option(x.Name, x.Id);
    }
    citis.onchange = function () {
        districtsSelect.length = 1;
        wardsSelect.length = 1;
        if (this.value != "") {
            const result = data.filter(n => n.Id === this.value);

            for (const k of result[0].Districts) {
                districtsSelect.options[districtsSelect.options.length] = new Option(k.Name, k.Id);
            }
        }
    };
    districtsSelect.onchange = function () {
        wardsSelect.length = 1;
        const dataCity = data.filter((n) => n.Id === citis.value);
        if (this.value != "") {
            const dataWards = dataCity[0].Districts.filter(n => n.Id === this.value)[0].Wards;

            for (const w of dataWards) {
                wardsSelect.options[wardsSelect.options.length] = new Option(w.Name, w.Id);
            }
        }
    };
}
