import React, { useEffect } from "react";
import '../../component/grid.css';
import '../home/home.css';
import '../../component/style.css';
import '../home/responsive.css';
import TruyenTheoTheLoai from "./truyentheotheloai";
import updateCartCount from '../updateCartCount';
import banner_home_1 from '../img/banner_home_1.jpg';
import banner_home_2 from '../img/banner_home_2.jpg';
import banner_home_3 from '../img/banner_home_3.jpg';
import banner_home_4 from '../img/banner_home_4.jpg';
import thangsachkimdong1 from '../img/thangsachkimdong1.jpg';
import thangsachkimdong2 from '../img/thangsachkimdong2.jpg';
import thangsachkimdong3 from '../img/thangsachkimdong3.jpg';
import thangsachkimdong4 from '../img/thangsachkimdong4.jpg';
import thangsachkimdong5 from '../img/thangsachkimdong5.jpg';
import sachmoi1 from '../img/sachmoi1.jpg';
import sachmoi2 from '../img/sachmoi2.jpg';
import sachmoi3 from '../img/sachmoi3.jpg';
import sachmoi4 from '../img/sachmoi4.jpg';
import sachmoi5 from '../img/sachmoi5.jpg';
import combo1 from '../img/combo1.jpg';
import combo2 from '../img/combo2.jpg';
import combo3 from '../img/combo3.jpg';
import combo4 from '../img/combo4.jpg';
import combo5 from '../img/combo5.jpg';
import manga_commic_1 from '../img/manga_comic_1.jpg';
import manga_commic_2 from '../img/manga_comic_2.jpg';
import manga_commic_3 from '../img/manga_comic_3.jpg';
import manga_commic_4 from '../img/manga_comic_4.jpg';
import manga_commic_5 from '../img/manga_comic_5.jpg';
import doraemon1 from '../img/doraemon1.jpg';
import doraemon2 from '../img/doraemon2.jpg';
import doraemon3 from '../img/doraemon3.jpg';
import doraemon4 from '../img/doraemon4.jpg';
import doraemon5 from '../img/doraemon5.jpg';
import wingsbooks1 from '../img/wings_book1.jpg';
import wingsbooks2 from '../img/wings_book2.jpg';
import wingsbooks3 from '../img/wings_book3.jpg';
import wingsbooks4 from '../img/wings_book4.jpg';
import wingsbooks5 from '../img/wings_book5.jpg';
import mua1tang1_1 from '../img/mua1tang1_1.jpg';
import mua1tang1_2 from '../img/mua1tang1_2.jpg';
import mua1tang1_3 from '../img/mua1tang1_3.jpg';
import mua1tang1_4 from '../img/mua1tang1_4.jpg';
import mua1tang1_5 from '../img/mua1tang1_5.jpg';


const Home = () => {
    useEffect(() => {
        updateCartCount();

        function checkScreenSize() {
            const checkSizeMobile = document.querySelectorAll('.checkSizeMobile');
            checkSizeMobile.forEach(item => {
                if (window.innerWidth > 520) {
                    item.classList.remove('c-6');
                    item.classList.add('c-4');
                } else {
                    item.classList.remove('c-4');
                    item.classList.add('c-6');
                }
            });
        }
        // Gọi function ngay khi component render
        checkScreenSize();

        // Lắng nghe sự kiện resize
        window.addEventListener('resize', checkScreenSize);
        return () => {
            window.removeEventListener('resize', checkScreenSize);
        };
    }, []);

    return (
        <>
            <TruyenTheoTheLoai matheloai="TL001" tieude="THÁNG SÁCH KIM ĐỒNG" />
            <TruyenTheoTheLoai matheloai="TL002" tieude="TRUYỆN TRANH NHẬT" />
            <TruyenTheoTheLoai matheloai="TL003" tieude="TRUYỆN NGÔN TÌNH" />
        </>
    )
}

export default Home;
