import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Api_product = () => {
    const { id_truyen } = useParams();  // Lấy id_truyen từ URL
    const [truyen, setTruyen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Gọi API để lấy dữ liệu của truyện
        axios.get(`http://localhost:3001/${id_truyen}`)
            .then(response => {
                setTruyen(response.data);
                setLoading(false);
            })
            .catch(err => {
                setError('Có lỗi xảy ra khi lấy dữ liệu');
                setLoading(false);
            });
    }, [id_truyen]);  // Chạy lại mỗi khi id_truyen thay đổi

    if (loading) {
        return <div>Đang tải...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    if (!truyen) {
        return <div>Không tìm thấy truyện</div>;
    }

    return (
        <div>
            <h1>{truyen.tentruyen}</h1>
        </div>
    );
}

export default Api_product;
