import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../shoppingcart/shoppingcart.css';
import '../../component/style.css';
import '../home/responsive.css';

function Shoppingcart() {

    const [cart, setCart] = useState({});

    useEffect(() => {
        const storedCart = JSON.parse(localStorage.getItem('GioHang')) || {};
        setCart(storedCart);
    }, []);

    function updateCart(newCart) {
        setCart(newCart);
        localStorage.setItem('GioHang', JSON.stringify(newCart));
    }

    function updateQuantity(productName, quantity) {
        if (quantity <= 0) return;

        const newCart = { ...cart };
        if (newCart[productName]) {
            newCart[productName].SoLuongTruyen = quantity;
            newCart[productName].TongTien = quantity * newCart[productName].GiaBanTruyen;
        }
        updateCart(newCart);
    }

    function removeFromCart(productName) {
        const newCart = { ...cart };
        delete newCart[productName];
        updateCart(newCart);
    }

    function getTamTinh() {
        return Object.values(cart).reduce((sum, item) => sum + item.TongTien, 0);
    }

    return (
        <div className="grid wide">
            <div className="row">
                <table className="tableCart">
                    <thead className="tieude">
                        <tr>
                            <th style={{ paddingBottom: 2 }} colSpan={2} className="text-center">
                                Sản phẩm
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Đơn giá
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Số lượng
                            </th>
                            <th style={{ paddingBottom: 2 }} className="text-center">
                                Tổng giá
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(cart).map((item) => (
                            <tr key={item} className="cart-product">
                                <td>
                                    <img className="cart-product-img" src={cart[item].AnhTruyen} alt={item} />
                                </td>
                                <td>
                                    <h3>{cart[item].TenTruyen}</h3>
                                    <button className="cart-product-remove" onClick={() => removeFromCart(item)}>Xóa</button>
                                </td>
                                <td className="text-center">
                                    {cart[item].GiaBanTruyen.toLocaleString('vi-VN')}đ
                                </td>
                                <td>
                                    <div className="ArrowInputTypeNumber">
                                        <button
                                            className="button-down flex-center"
                                            onClick={() => updateQuantity(item, cart[item].SoLuongTruyen - 1)}
                                        >
                                            <i className="fas fa-chevron-down"></i>
                                        </button>
                                        <input
                                            type="text"
                                            className="input-soluong"
                                            value={cart[item].SoLuongTruyen}
                                            onChange={(e) => updateQuantity(item, parseInt(e.target.value) || 1)}
                                        />
                                        <button
                                            className="button-up flex-center"
                                            onClick={() => updateQuantity(item, cart[item].SoLuongTruyen + 1)}
                                        >
                                            <i className="fas fa-chevron-up"></i>
                                        </button>
                                    </div>
                                </td>
                                <td className="text-center">
                                    {cart[item].TongTien.toLocaleString('vi-VN')}đ
                                </td>
                            </tr>
                        ))}
                    </tbody>

                </table>
            </div>
            <div className="row">
                <div
                    className="col c-o-4 c-8 m-o-8 m-4 l-o-9 l-3"
                    style={{
                        display: "inline-flex",
                        lineHeight: 3,
                        justifyContent: "flex-end"
                    }}
                >
                    <p
                        className="flex-center">Tạm tính: &nbsp;
                    </p>
                    <b>
                        <p className="tamtinh">0đ</p>
                    </b>
                </div>
                <div className="col c-o-8 c-4 m-o-8 m-4 l-o-9 l-3">
                    <button id="thanhtoan" type="submit">
                        <b>Thanh toán</b>
                    </button>
                </div>
            </div>
        </div>

    )
}
export default Shoppingcart;
